CREATE TABLE IF NOT EXISTS `course` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `name` varchar(128) NOT NULL,
  `en_name` varchar(128) NOT NULL,
  `desc` text NOT NULL,
  `en_desc` text NOT NULL,
  `display_order` int(8) NOT NULL,
  `is_top` tinyint(2) NOT NULL DEFAULT '0',
  `recommand_pic` varchar(128) NOT NULL,
  `en_recommand_pic` varchar(128) NOT NULL,
  `pdf` varchar(128) NOT NULL,
  `en_pdf` varchar(128) NOT NULL,
  `pdf_name` varchar(64) NOT NULL,
  `en_pdf_name` varchar(64) NOT NULL,
