  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=12 ;

--
-- 转存表中的数据 `course`
--

INSERT INTO `course` (`id`, `name`, `en_name`, `desc`, `en_desc`, `display_order`, `is_top`, `recommand_pic`, `en_recommand_pic`, `pdf`, `en_pdf`, `pdf_name`, `en_pdf_name`, `createtime`) VALUES
(1, '免费试听课', 'Free Trail', '<p><span style="color: rgb(255, 140, 0);"><strong><span style="font-size: 18px;"><span style="font-family: arial, helvetica, sans-serif;">&nbsp; 精品课程</span></span></strong></span></p><p><span style="font-size: 16px;"><span style="font-family: arial, helvetica, sans-serif;">&nbsp;&nbsp;&nbsp; <span style="color: rgb(0, 0, 0);">Rolling Music提供立体化创意课程和线上线下完整教学体系：统一教案，精确到分阶梯式学习，逐级强化，实现精准化教学；在线下载音视频库和教学文件，教师实时掌握学员学习动态，实现成效化学习；舞台表演、社会实践辅助教学，实现立体化训练；线上教务系统查询课程、完成作业、师生评定，实现信息化管理。</span></span></span></p><p><strong><span style="font-size: 18px;"><span style="color: rgb(255, 140, 0);"><span style="font-family: arial, helvetica, sans-serif;">&nbsp; 免费试听</span></span></span></strong></p><p><span style="color: rgb(0, 0, 0);"><span style="font-size: 16px;"><span style="font-family: arial, helvetica, sans-serif;">&nbsp;&nbsp; · RM 免费试听课包含启蒙鼓（儿童）、架子鼓、吉他、贝斯、尤克里里、流行钢琴等</span></span></span></p><p><span style="color: rgb(0, 0, 0);"><span style="font-size: 16px;"><span style="font-family: arial, helvetica, sans-serif;">&nbsp;&nbsp; · 试听时间：启蒙鼓（儿童）30分钟、其他课程15分钟</span></span></span></p><p><span style="color: rgb(0, 0, 0);"><span style="font-size: 16px;"><span style="font-family: arial, helvetica, sans-serif;">&nbsp;&nbsp; · 乐器由RM免费提供</span></span></span></p><p><span style="color: rgb(0, 0, 0);"><span style="font-size: 16px;"><span style="font-family: arial, helvetica, sans-serif;">&nbsp;&nbsp; · 点击下方“立即试听”提前预约，课程顾问会及时与您取得联系哦！</span></span></span></p><p><br></p>', '<p><br><span style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;">Premium Courses</span></p><div paragraphname="paragraph2" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><br></div><div paragraphname="paragraph2" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><span id="noHighlight_0.3675510010216385">Rolling music provides three-dimensional creative courses and a</span>&nbsp;complete online teaching system, this includes: a standard lesson plan, levels varying in difficulty to help develop the student''s musicianship step by step, live performances, assistant teaching to help develop student''s understanding of theory, online administration, complete homework, and teacher-student evaluation.</div><br style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><div paragraphname="paragraph4" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><span id="noHighlight_0.06498187012039125">Free Trial Lesson</span></div><div paragraphname="paragraph4" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><br></div><div paragraphname="paragraph6" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><span id="noHighlight_0.2610254934988916">· RM free trial lectures include the Introduction to Drums (children), Drum kit, Guitar, Bass, Ukulele, pop piano, etc.</span></div><br style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><div paragraphname="paragraph8" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><span id="noHighlight_0.37362219113856554">· Audition Time: Intrduction to Drums (children) 30 minutes, other courses 15 mins</span></div><br style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><div paragraphname="paragraph10" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><span id="noHighlight_0.014918519649654627">· Instruments are provided free of charge by RM</span></div><br style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><div paragraphname="paragraph12" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><span id="noHighlight_0.1011631228029728">· Click on the "immediately try to listen" to book in advance, Course Advisor will be in time to get in touch with you</span></div>', 1, 1, '/uploads/20161209967364Zcjf7pu5.jpg', '', '/uploads/20161209967364Zcjf7pu5.jpg', '', '', '', '2016-12-03 23:57:25'),
(2, '架子鼓', 'Drums', '<p><span style="color: rgb(105, 105, 105);"><span style="font-size: 16px;"><span style="font-family: arial, helvetica, sans-serif;">&nbsp;&nbsp;&nbsp;&nbsp;</span></span></span><span style="color: rgb(0, 0, 0);"><span style="font-size: 16px;"><span style="font-family: arial, helvetica, sans-serif;">&nbsp; <span style="color: rgb(0, 0, 0);"><span style="font-size: 16px;"><span style="font-family: arial, helvetica, sans-serif;"><img style="margin: 5px; width: 1200px; height: 854px;" alt="" src="/uploads/images/%E5%9B%BE%E7%89%871%20(2)_%E5%89%AF%E6%9C%AC.png" data-cke-saved-src="/uploads/images/%E5%9B%BE%E7%89%871%20(2)_%E5%89%AF%E6%9C%AC.png"></span></span></span></span></span></span></p><p><strong><span style="color: rgb(255, 140, 0);"><span style="font-size: 18px;"><span style="font-family: arial, helvetica, sans-serif;">&nbsp;课程介绍</span></span></span></strong></p><p><span style="color: rgb(0, 0, 0);"><span style="font-size: 16px;"><span style="font-family: arial, helvetica, sans-serif;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 16px;"><span style="text-transform: none; line-height: 21px; text-indent: 0px; letter-spacing: normal; font-family: Helvetica, Arial, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; word-spacing: 0px; float: none; display: inline !important; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); -webkit-text-stroke-width: 0px;">&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style="text-transform: none; line-height: 21px; text-indent: 0px; letter-spacing: normal; font-family: Helvetica, Arial, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; word-spacing: 0px; float: none; display: inline !important; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); -webkit-text-stroke-width: 0px;">RM将架子鼓课分为</span></span></span></span></span><span style="color: rgb(255, 140, 0);"><span style="font-size: 16px;"><span style="font-family: arial, helvetica, sans-serif;"><span style="text-transform: none; line-height: 21px; text-indent: 0px; letter-spacing: normal; font-family: Helvetica, Arial, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; word-spacing: 0px; float: none; display: inline !important; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); -webkit-text-stroke-width: 0px;">学龄前儿童（4-6周岁）音乐启蒙</span></span></span></span><span style="color: rgb(0, 0, 0);"><span style="font-size: 16px;"><span style="font-family: arial, helvetica, sans-serif;"><span style="color: rgb(0, 0, 0);"><span style="text-transform: none; line-height: 21px; text-indent: 0px; letter-spacing: normal; font-family: Helvetica, Arial, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; word-spacing: 0px; float: none; display: inline !important; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); -webkit-text-stroke-width: 0px;">和</span></span></span></span></span><span style="color: rgb(255, 140, 0);"><span style="font-size: 16px;"><span style="font-family: arial, helvetica, sans-serif;"><span style="text-transform: none; line-height: 21px; text-indent: 0px; letter-spacing: normal; font-family: Helvetica, Arial, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; word-spacing: 0px; float: none; display: inline !important; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); -webkit-text-stroke-width: 0px;">7周岁以上青少年儿童及成人常规课程</span></span></span></span><span style="color: rgb(0, 0, 0);"><span style="font-size: 16px;"><span style="font-family: arial, helvetica, sans-serif;"><span style="color: rgb(0, 0, 0);"><span style="text-transform: none; line-height: 21px; text-indent: 0px; letter-spacing: normal; font-family: Helvetica, Arial, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; word-spacing: 0px; float: none; display: inline !important; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); -webkit-text-stroke-width: 0px;">两个阶段。前者注重音乐的感受与认知，通过趣味电子教学软件培养孩子的乐感、节奏感，掌握基本演奏方法，培养学习兴趣。常规课程则注重技巧和音乐理论的深入学习，不同曲风的演绎，完整曲目的演奏，或根据学生需求进行考级曲目培训。</span></span> </span></span></span></p><p><br></p><p><span style="color: rgb(0, 0, 0);"><span style="font-size: 16px;"><span style="font-family: arial, helvetica, sans-serif;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 16px;"><span style="text-transform: none; line-height: 21px; text-indent: 0px; letter-spacing: normal; font-family: Helvetica, Arial, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; word-spacing: 0px; float: none; display: inline !important; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); -webkit-text-stroke-width: 0px;"><img style="border-width: 0px; border-style: solid; margin: 5px; width: 1200px; height: 247px;" alt="" src="/uploads/images/%E6%9C%80%E6%96%B0(1).jpg" data-cke-saved-src="/uploads/images/%E6%9C%80%E6%96%B0(1).jpg">​</span></span></span></span></span></span></p><p><span style="color: rgb(0, 0, 0);"><span style="font-size: 16px;"><span style="font-family: arial, helvetica, sans-serif;">&nbsp;&nbsp;&nbsp; </span></span></span></p><p data-cke-expando="undefined"><strong><span style="color: rgb(255, 140, 0);"><span style="font-size: 18px;"><span style="font-family: arial, helvetica, sans-serif;" data-cke-expando="undefined">关于架子鼓</span></span></span></strong></p><p data-cke-expando="undefined"><span style="color: rgb(0, 0, 0);" data-cke-expando="undefined"><span style="font-size: 16px;" data-cke-expando="undefined"><span style="font-family: arial, helvetica, sans-serif;" data-cke-expando="undefined">&nbsp;&nbsp;&nbsp; 架子鼓即爵士鼓，是爵士乐队中十分重要的一种打击乐器，它通常由一个脚踏的低音大鼓（Bass Drum，又称“底鼓”）、一个军鼓、二个或以上嗵嗵鼓（Tom-Tom Drum）、一个或两个吊镲（Crash Cymbal）、一个节奏镲（Ride Cymbal）和一个带踏板的踩镲（Hi-Hat）等部分组成.。鼓手用鼓槌击打各部件使其发声。爵士乐中常用的鼓槌有木制的鼓棒，由钢丝制成的鼓刷，由一捆细木条捆成的束棒等。</span></span></span></p><p><span style="font-family: arial, helvetica, sans-serif;"><span style="font-size: 16px;"><span style="color: rgb(0, 0, 0);"><font color="#000000">&nbsp;&nbsp;&nbsp; 架子鼓对于演奏者的注意力集中和分配能力、身体协调性、节奏感都有很高的要求，学习架子鼓可以培养青少年儿童的注意力，听力，模仿力，创造力，开发左右脑，同时能帮助青少儿儿童树立自信心，培养开朗热情的性格品质。对于成年人和学业压力较重的学生而言，架子鼓更是舒缓压力，释放情绪的出口，十分有利于演奏者的身心健康。</font></span></span></span></p><p><br></p><p><span style="color: rgb(0, 0, 0);"><span style="font-size: 16px;"><span style="text-transform: none; line-height: 21px; text-indent: 0px; letter-spacing: normal; font-family: Helvetica, Arial, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; word-spacing: 0px; float: none; display: inline !important; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); -webkit-text-stroke-width: 0px;">&nbsp;&nbsp;&nbsp;</span></span></span></p>', '<p><br><span style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;">Course Introduction</span></p><br style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><div paragraphname="paragraph2" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><span id="noHighlight_0.3191208317875862">RM divides the Drum class into two stages: 1. Preschool children (4-6 years old) Introduction to Music 2. A</span>dolescent and Adult courses (7 years of age and above) . Courses for preschool children pay attention to appraising and feeling music, this course is taught through an eletronic software that uses animations to spark interest from and engage the young students.&nbsp;<span id="noHighlight_0.3191208317875862">A</span>dolescent and Adult courses&nbsp;focus on skills and music theory in-depth, as wekk as different styles of music, and creating &nbsp;musical repertoire for performance, or treaining student to pass a given music examination.</div><p><br style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><br style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"></p><div paragraphname="paragraph6" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><br></div><p><br style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><br style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><br style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"></p><div paragraphname="paragraph10" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><span id="noHighlight_0.8273594127967954">About the Drums</span></div><br style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><div paragraphname="paragraph12" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><span id="noHighlight_0.34814175218343735">The drums are an essential peice of any band, they provide rhythm and, together with the bass, act as the backbone of the band giving the feel. The drum set usually consists of a Bass drum (together with a Bass drum pedal), a snare drum, 3 tom toms, a hi-hat stand with the hi-hat cymbals, and 2 cymbal stands, one with a ride cymbal, and the other with a crash cymbal. The drummer uses a drum stick to hit the drums which creates sound, drummers may use alternative methods to play the drum for example brushes which are a bundle a steele wires that are "brushed" against the drum to provide a warm tone, or they might use Hotrods which are a bundle of smaller sticks which provide a more quiet sound, or some drummers even use their hands!</span></div><br style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><div paragraphname="paragraph14" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><span id="noHighlight_0.5491021599154919">The drum is focused body coordination and rhythm, learning drum can develop young children''s attention, listening skills, imitation, creativity, and also self-confidence. For adults and students with heavier academic stress, the drum can help sooth pressure, release emotiona which is very beneficial to the player''s physical and mental health.</span></div>', 2, 1, '/uploads/201612091085OlFZMw6pQX.jpg', '', '/uploads/201612091085OlFZMw6pQX.jpg', '', '', '', '2016-12-04 00:14:53'),
(3, '吉他', 'Guitar', '<p><img style="WIDTH: 1224px; HEIGHT: 714px; MARGIN-LEFT: 5px; MARGIN-RIGHT: 5px" alt="" src="/uploads/images/%E5%9B%BE%E7%89%8712.png" data-cke-saved-src="/uploads/images/%E5%9B%BE%E7%89%8712.png"><img style="WIDTH: 1224px; HEIGHT: 683px; MARGIN-LEFT: 5px; MARGIN-RIGHT: 5px" alt="" src="/uploads/images/%E5%9B%BE%E7%89%8711(1).png" data-cke-saved-src="/uploads/images/%E5%9B%BE%E7%89%8711(1).png"></p><p data-cke-expando="undefined"><br></p><p data-cke-expando="undefined"><strong><span style="COLOR: #ff8c00"><span style="FONT-SIZE: 16px">课程介绍</span></span></strong></p><p data-cke-expando="undefined"><span style="COLOR: #000000"><span style="FONT-SIZE: 16px">&nbsp;&nbsp;&nbsp; RM吉他课程针对7周岁以上青少年儿童和成年人，学生可选择民谣吉他或电吉他。前者偏向民谣弹唱，电箱琴满足学员的表演需求，后者作为乐队中不可或缺的乐器，更加注重乐队协作与个人演奏的平衡。处于课程分为8个等级，每个等级16课时，可根据学生意愿选择音乐等级认证。</span></span></p><p data-cke-expando="undefined"><img style="margin: 5px 10px; width: 1000px; height: 284px;" alt="" src="/uploads/images/%E6%9C%80%E6%96%B02.jpg" data-cke-saved-src="/uploads/images/%E6%9C%80%E6%96%B02.jpg"></p><p data-cke-expando="undefined"><br></p><p data-cke-expando="undefined"><span style="COLOR: #ff8c00"><strong><span style="FONT-SIZE: 16px">关于吉他</span></strong></span></p><p data-cke-expando="undefined"><span style="FONT-SIZE: 16px"><span style="LINE-HEIGHT: 24px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); FONT-VARIANT: normal; FONT-STYLE: normal; TEXT-INDENT: 28px; DISPLAY: inline !important; FONT-FAMILY: arial, sans-serif; WHITE-SPACE: normal; FLOAT: none; LETTER-SPACING: normal; COLOR: rgb(51,51,51); FONT-WEIGHT: normal; WORD-SPACING: 0px; -webkit-text-stroke-width: 0px">&nbsp;&nbsp;&nbsp; 吉他，属于弹拨乐器，通常有六条弦，形状与提琴相似。吉他在流行音乐、摇滚音乐、蓝调、民歌、费拉门戈中，常被视为主要乐器。而在古典音乐的领域里，吉他常以独奏或二重奏的型式演出；当然，在室内乐和管弦乐中，吉他亦扮演着相当程度的陪衬角色。</span></span></p><p data-cke-expando="undefined"><span style="FONT-SIZE: 16px"><span style="LINE-HEIGHT: 24px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); FONT-VARIANT: normal; FONT-STYLE: normal; TEXT-INDENT: 28px; DISPLAY: inline !important; FONT-FAMILY: arial, sans-serif; WHITE-SPACE: normal; FLOAT: none; LETTER-SPACING: normal; COLOR: rgb(51,51,51); FONT-WEIGHT: normal; WORD-SPACING: 0px; -webkit-text-stroke-width: 0px" data-cke-expando="undefined">&nbsp;&nbsp;&nbsp; 吉他便携，且对演奏环境要求低，可与他人合作演奏，也可自弹自唱，因此受到许多人的欢迎和喜爱。弹奏吉他可以锻炼手指灵活度，开发左右脑，更可以增加个人魅力，找到情绪的抒发口，缓解压力，有益于身心健康。</span></span></p>', '<p><span style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;">Course Introduction</span></p><br style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><div paragraphname="paragraph2" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><span id="noHighlight_0.5301895381417125">The RM Guitar Course is designed for students 7-years-old and up, students can choose to study acoustic guitar or electric guitar. Acoustic guitar favours solo preformances where the student both sings and plays guitar, electric guitar favours band preformances with other musicians. The RM guitar course is divided into 8 grades, each grade containing 16 lessons, if students wish to take a musical examination (such as Rock School, MIDI Level, ABRSM, etc.) Rolling Music can provide guidance in achieving high marks in such examinations.</span></div><p><br style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><br style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><br style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><br></p><div paragraphname="paragraph8" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><span id="noHighlight_0.6058166401926428">About the Guitar</span></div><br style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><div paragraphname="paragraph10" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><span id="noHighlight_0.09754496347159147">Guitars, belonging to the string family, they usually have six strings, they are similar to the shape of a violin, and they are usually played by strumming or plucking the strings. Guitars play major roles in Pop music, Rock, Blues, Folk, and Latin music mainly Flamingo. In Classical music, the guitar is often performed as solo or duet preformance, and of course, in chamber music and orchestra, the guitar also plays a considerable role.</span></div><div paragraphname="paragraph12" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><br></div><div paragraphname="paragraph12" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><span id="noHighlight_0.29536330234259367">The guitar is portable, and has low requirements for preformance equipment, it can can played with others, and also can be played by itself, so it is a widley loved instrument. Playing the guitar can exercise finger flexibility, development around the brain especially in coordiation, but also to increase one''s personal charm. The guitar can also act as a source emotional expression accompanied by singing and also can help relieve stress, hence it is beneficial both phisically and mentally.</span></div><p><br style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><br></p>', 3, 0, '', '', '', '', '', '', '2016-12-04 00:15:11'),
(4, '贝斯', 'Bass', '<p><br></p><p><strong><span style="COLOR: #ff8c00"><span style="FONT-SIZE: 20px"><img style="border-width: 0px; border-style: solid; border-color: initial; margin: 5px 10px; width: 1100px; height: 413px;" alt="" src="/uploads/images/20111127201820_nwr5x_thumb_600_0.jpg" data-cke-saved-src="/uploads/images/20111127201820_nwr5x_thumb_600_0.jpg"></span></span></strong></p><p data-cke-expando="undefined"><strong data-cke-expando="undefined"><span style="COLOR: #ff8c00" data-cke-expando="undefined"><span style="FONT-SIZE: 20px" data-cke-expando="undefined">课程介绍</span></span></strong></p><p><span style="color:#000000;"><strong><span style="FONT-SIZE: 20px">&nbsp; &nbsp;</span></strong><span style="FONT-SIZE: 16px">RM贝斯课程针对7周岁以上青少年儿童和成年人，现代音乐乐队中贝斯是必不可少的乐器之一，它在乐队中主要担任低音声部，有时也作即兴独奏。在乐队中与鼓相互辉映，共同为音乐铺垫基础节奏并加以修饰和点缀。课程分为8个等级，每个等级16课时，可根据学生意愿选择音乐等级认证。</span></span></p><p data-cke-expando="undefined"><br></p><p data-cke-expando="undefined"><span style="FONT-SIZE: 16px" data-cke-expando="undefined"><img style="width: 1000px; margin-left: 10px; margin-right: 10px; height: 284px;" alt="" src="/uploads/images/%E6%9C%80%E6%96%B02.jpg" data-cke-saved-src="/uploads/images/%E6%9C%80%E6%96%B02.jpg"></span></p><p><span style="FONT-SIZE: 20px"><strong><span style="COLOR: #ff8c00">关于贝斯</span></strong></span></p><p><span style="color:#000000;"><span style="FONT-SIZE: 16px">&nbsp; &nbsp; 贝斯，属于弹拨乐器，通常只有四弦，以吉他的后四根弦低一个八度定音，即E,A,D,G。作为电声乐器，音量变化可以调节。中低音区发音厚实响亮，高音区则较明亮。同吉他一样可以运用多种技巧，演奏出特色旋律来，因此电贝斯在合奏中是不仅仅作为低音伴奏乐器而出现的，它往往奏出令人难忘 的乐句，音色优美，旋律流畅，质感很强，变化丰富。</span></span></p><p><span style="color:#000000;"><span style="FONT-SIZE: 16px">&nbsp; &nbsp; 界内通说，学吉他，世界上可能会多了一名好吉他手；学好贝斯，世界上可能会多了一支好乐队。贝斯独奏音色低沉，一方面非常具有抒情的表现力，用贝斯演奏爵士乐非常有味道，另外由于贝斯节奏感强烈，在funk乐中的表现非同一般，而现在越来越受重视的metal中，贝斯更是不可或缺，无论是新金属还是传统的金属乐，贝斯都和鼓一样都是乐队的节奏和基础。</span></span></p><p><br></p>', '<p><span style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;">Course Introduction</span></p><br style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><div paragraphname="paragraph2" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><span id="noHighlight_0.7708582228515297">The RM Bass Course is designed for students 7-years-old and above, the bass is one of the essential instruments in modern music, as it usually plays the bass part (hence the name) providing support for a whole ensemble, the bass can&nbsp;even be played as a solo instrument! The bass, together with the drums, provide the rhythm and feel in most music genres, such as funk, rock, jazz and much more! The RM bass course is divided into 8 grades, each grade consists of 16 lessons. I</span>f students wish to take a musical examination (such as Rock School, MIDI Level, ABRSM, etc.) Rolling Music can provide guidance in achieving high marks in such examinations.</div><div>&nbsp; &nbsp;</div><div><br></div><div><br></div><div><br></div><br style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><div paragraphname="paragraph8" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><span id="noHighlight_0.06685860874131322">About the Bass</span></div><br style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><div paragraphname="paragraph10" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><span id="noHighlight_0.8549865435343236">The bass, which belongs to the string family, usually has only four strings and has a dark sound. As an electric instrument, the volume can be adjusted to fit in any performance situation. The timbre of the bass is thicker and it has a lower register. Like the guitar, it can be used a variety of ways, for example, playing a distinctive melody, playing a bass part, playing chords, etc.&nbsp;</span></div><div paragraphname="paragraph10" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><br></div><p>There is a saying in the music industry: learn the guitar and the world will have another good guitarist, learn the bass and the world will have another good band. The&nbsp;<span style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;">Bass as a solo instrument can be used as a medium to express a variety of&nbsp;emotions. In funk, the bass is largely responsible for providing the strong pulsating riffs that make the music "funky". In Metal the bass provides the deep undertones that are signature to metal. Bass provides the foundation for all genres of music and supports the rest of the band.</span></p>', 4, 0, '', '', '', '', '', '', '2016-12-04 00:15:33'),
(5, '尤克里里', 'Ukulele', '<p><img style="MARGIN: 5px; WIDTH: 1204px; HEIGHT: 500px" alt="" src="/uploads/images/1603462.jpg" data-cke-saved-src="/uploads/images/1603462.jpg"></p><p><strong><span style="COLOR: #ff8c00"><span style="FONT-SIZE: 20px">课程介绍</span></span></strong></p><p><span style="FONT-SIZE: 16px">&nbsp; &nbsp;<span style="COLOR: #000000">RM尤克里里课程针对4周岁以上青少年儿童和成年人。尤克里里归属于吉他乐器一族，音色好听可爱，能够激发节奏潜能，可以弹唱，也可以独奏。课程分为8个等级，每个等级16课时。</span></span></p><p><span style="FONT-SIZE: 16px"><span style="COLOR: #000000">​</span><img style="MARGIN: 5px; WIDTH: 1000px; HEIGHT: 281px" alt="" src="/uploads/images/%E6%9C%80%E6%96%B03.jpg" data-cke-saved-src="/uploads/images/%E6%9C%80%E6%96%B03.jpg">​</span></p><p><strong><span style="COLOR: #ff8c00"><span style="FONT-SIZE: 20px">关于尤克里里</span></span></strong></p><p><span style="FONT-SIZE: 16px">&nbsp; &nbsp;<span style="COLOR: #000000">尤克里里即夏威夷小吉他，是一种四弦夏威夷的拨弦乐器，发明于葡萄牙盛行于夏威夷，归属于吉他乐器一族。它可以进行合奏，重奏，也可独奏，演奏花样繁多，基本定弦为G,C,E,A,琴弦为尼龙弦，右手演奏时可留指甲或不留，演奏姿势将琴体轻松贴在靠在前胸。弹奏要点是和弦和节奏型。</span></span></p><p><span style="COLOR: #000000"><span style="FONT-SIZE: 16px">&nbsp; &nbsp;尤克里里比吉他少了两根弦，学习成本直线下降，而其演奏效果却还和吉他差不多。音色清脆入耳，弹起来节奏活泼畅快，可以说是居家旅行必备之良品。且绝大多数尤克里里的弦是尼龙弦，按起来不疼，对手指特别友好。</span></span></p><p><span style="COLOR: #000000"><span style="FONT-SIZE: 16px">&nbsp; &nbsp;</span></span></p>', '<p><span style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;">Course Introduction</span></p><br style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><div paragraphname="paragraph2" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><span id="noHighlight_0.533079675398767">The RM Ukulele course is for any students above the age of 4. Ukulele belongs to the string family and has a bright tone. The RM Ukulele course is divided into 8 grades, each grade consists of 16 lessons.</span></div><br style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><div paragraphname="paragraph4" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><br></div><div paragraphname="paragraph6" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><span id="noHighlight_0.663335953373462">About the Ukulele</span></div><br style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><div paragraphname="paragraph8" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><span id="noHighlight_0.9252175535075366">Ukulele is a small Hawaiian guitar-like instrument, it has 4 strings with the top string being the highest string then the second string being the lowest then ascending from there. It can be performed in ensembles or as a solo instrument. The basic tuning follows this order: G, C, E, then A. The strings are made from nylon string, which makes it easier to play for younger students.</span></div><br style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><div paragraphname="paragraph10" style="color: rgb(85, 85, 85); font-family: ''Segoe UI'', Tahoma, Helvetica, sans-serif; font-size: 13px;"><span id="noHighlight_0.07792287203483284">Ukulele has fewer strings than a guitar, learning Ukulele is the cheaper choice when choosing between the two, additionally, it''s performance is almost as good as a performance on the guitar. The Ukulele has a crisp tone and can play lively&nbsp;rhythms, it''s a good instrument to be played at home and on the road.</span></div>', 5, 0, '', '', '', '', '', '', '2016-12-04 00:16:00'),
(6, '流行钢琴', 'Keybord', '<p><img alt="" style="width: 1204px; height: 760px; margin: 5px;" data-cke-saved-src="/uploads/images/02220261.jpg" src="/uploads/images/02220261.jpg"><span style="font-size:20px;"><b style="color: rgb(255, 140, 0); font-family: Arial; font-size: 20px; -webkit-text-stroke-color: rgb(255, 140, 0); -webkit-text-stroke-width: initial;">课程介绍</b></span></p><p style="margin: 0px; font-size: 16px; line-height: normal; font-family: Arial; color: rgb(35, 35, 35); -webkit-text-stroke-color: rgb(35, 35, 35); -webkit-text-stroke-width: initial;"><span style="font-size: 20px; line-height: normal; -webkit-font-kerning: none; color: rgb(255, 140, 0); -webkit-text-stroke-color: rgb(255, 140, 0);"><b>&nbsp; &nbsp;</b></span><span style="font-kerning: none"><span style="font-size:16px;">RM流行钢琴课程针对4周岁以上青少年儿童和成年人，流行钢琴以古典钢琴为基调，又区别于古典钢琴，基本音阶练习熟练的基础上，选取耳熟能详的流行音乐教授演奏，风格多样，使学员在学习过程中感受到键盘带来的乐趣。课程分为8个等级，每个等级16课时，可根据学生意愿选</span>择音乐等级认证。</span></p><p style="margin: 0px; font-size: 16px; line-height: normal; font-family: Arial; color: rgb(35, 35, 35); -webkit-text-stroke-color: rgb(35, 35, 35); -webkit-text-stroke-width: initial;"><span style="font-kerning: none">​</span></p><p style="margin: 0px; font-size: 16px; line-height: normal; font-family: Arial; color: rgb(35, 35, 35); -webkit-text-stroke-color: rgb(35, 35, 35); -webkit-text-stroke-width: initial;"><img alt="" style="width: 1000px; height: 281px;" data-cke-saved-src="/uploads/images/%E6%9C%80%E6%96%B03.jpg" src="/uploads/images/%E6%9C%80%E6%96%B03.jpg">​</p><p style="margin: 0px; font-size: 16px; line-height: normal; font-family: Arial; color: rgb(35, 35, 35); -webkit-text-stroke-color: rgb(35, 35, 35); -webkit-text-stroke-width: initial;"><br></p><p style="margin: 0px; font-size: 20px; line-height: normal; font-family: Arial; color: rgb(255, 140, 0); -webkit-text-stroke-color: rgb(255, 140, 0); -webkit-text-stroke-width: initial;"><span style="font-kerning: none"><b>关于流行钢琴&nbsp;</b></span></p><p style="margin: 0px; font-size: 16px; line-height: normal; font-family: Arial; color: rgb(35, 35, 35); -webkit-text-stroke-color: rgb(35, 35, 35); -webkit-text-stroke-width: initial;"><span style="font-kerning: none">&nbsp; &nbsp;钢琴是西洋古典音乐中的一种键盘乐器，有“乐器之王”的美称。音域范围几乎囊括了乐音体系中的全部乐音，是除了管风琴以外音域最广的乐器。流行钢琴作为古典钢琴的延伸，旋律顺口，易于传播，容易打动人心。</span></p><p style="margin: 0px; font-size: 16px; line-height: normal; font-family: Arial; color: rgb(35, 35, 35); -webkit-text-stroke-color: rgb(35, 35, 35); -webkit-text-stroke-width: initial;"><span style="font-kerning: none">&nbsp; &nbsp; 双手手指的频繁活动促使大脑更活跃，使他们大多学习起来很轻松。双手的协调合作以及脚部踏板的配合则使左右脑功能同时得到开发，身体各部分的协作能力更佳。而且能修身养性，提高素质，陶冶性情，调剂紧张的学习，工作，缓解生活中的压力。 &nbsp; &nbsp; &nbsp;</span></p>', '<div>Course Introduction</div><div><br></div><div>&nbsp; &nbsp;RM Popular Piano Course is designed for any student above the age of 4, the popular piano uses the same piano as the&nbsp;classical piano, but has a different performance style compared to classical piano performances. Like in classical music students will learn the fundamentals of piano theory, then students will choose a style of Pop music that they are familiar to&nbsp;and study this style on the piano. The course is divided into eight levels, each lever consists of 16 lessons, according to the wishes of students choose music level certification.&nbsp;<span id="noHighlight_0.7708582228515297" style="color: rgb(85, 85, 85); font-family: &quot;Segoe UI&quot;, Tahoma, Helvetica, sans-serif; font-size: 13px;">&nbsp;I</span><span style="color: rgb(85, 85, 85); font-family: &quot;Segoe UI&quot;, Tahoma, Helvetica, sans-serif; font-size: 13px;">f students wish to take a musical examination (such as Rock School, MIDI Level, ABRSM, etc.) Rolling Music can provide guidance in achieving high marks in such examinations.</span></div><div>&nbsp; &nbsp;</div><div><br></div><div><br></div><div>About the popular piano</div><div>&nbsp; &nbsp;The<span style="font-size:12px;"> pia</span>no<span style="font-size:12px;"><span style="font-family: sans-serif;">&nbsp;is an acoustic, stringed&nbsp;musical&nbsp;instrument</span>&nbsp;</span>invented in the 1700s, it has earned the title of the "King of Instruments" due to its ability to apply itself to almost all styles of music. Popular piano is one of these applications it uses simple melodies as they are catchy and popular piano performances can easily evoke strong emotions from the listeners.</div><div><br></div><div>&nbsp; &nbsp; Both hands need to play different things which stimulates brain&nbsp;activity so is makes learning new things easier. Piano playing challenges cooperation between the hands and the feet on the pedals, with forces the left and right brain to function at the same time, this helps develop&nbsp;body coordination and the ability to multitask. But also playing piano can serve as a way to let off steam and ease the pressures of life.</div><div><br></div>', 6, 0, '', '', '', '', '', '', '2016-12-04 00:16:34'),
(9, '乐队合奏', 'Band Couse', '<p>&nbsp; &nbsp; <img alt="" style="width: 1200px; height: 777px; margin: 5px;" data-cke-saved-src="/uploads/images/396924_020306132182_2%20(1).jpg" src="/uploads/images/396924_020306132182_2%20(1).jpg"></p><p><span style="color:#000000;">&nbsp; &nbsp;</span></p><p><span style="color:#000000;">&nbsp; &nbsp; Rolling Music热迷音乐乐队课程以音乐学习的互动性和趣味性为教学特色，通过乐器技巧、音乐欣赏与团队合作结合的双语教学方式，提高学生的合作与社交能力，问题处理能力，艺术鉴赏力、创造力和自信心。此课程已与国际学校、国际社区、培训中心、留学机构等合作，打造多支学生乐队，参加大小型活动演出，获得家长和学生的一致好评。它不仅可以推动学生的身心发展，探索培养艺术领域的顶尖后备人才、提高学生艺术团体的建设水平、更为学校或机构打造开放而独树一帜的文教结合创新品牌。</span></p><p><br></p><p><span style="font-size:16px;"><span style="color:#ff8c00;"><strong>课程内容</strong></span></span></p><p><span style="color:#000000;">&nbsp;&nbsp;乐队班教师将向学员展示何为乐队、如何团队合作、如何进行音乐社交，并向其全面普及音乐知识，带学生了解不同的音乐风格、赏析代表乐队和作品、学习乐器在不同音乐风格中的应用和演奏技巧，使学生对于音乐有更深入的了解和更立体的感知，从而帮助乐队确定风格。继而指导学生乐队编曲、作词以及合奏的技巧，包括团队合奏中的非语言交流、舞台演出技巧、危机处理与临场应变、排练要领、乐队包装、形象定位、乐队管理等等。适合有器乐基础的学员，也可为整支乐队提供创作和表演辅导。</span></p><p><br></p><p><span style="color:#ff8c00;"><span style="font-size:16px;"><strong>课程优势</strong></span></span></p><p><span style="color:#000000;">1）音乐知识的立体化、多元化学习</span></p><p><span style="color:#000000;">&nbsp;&nbsp;&nbsp;学生将通过视、听、说、学、奏等方式全面了解音乐领域内的多种文化和风格，深入了解不同乐器的音色、音域等特性，使学生走出乐器演奏的单一性，丰富知识，开拓眼界，站在更全面、更高层次的角度理解和学习音乐。</span></p><p><span style="color:#000000;">2）社交能力与团队合作意识的培养</span></p><p><span style="color:#000000;">&nbsp;&nbsp;&nbsp;RM以“乐队教乐队”的形式手把手培养学生的合作意识和社交能力，通过游戏、命题创作、排练等方式加强团队建设，引导学生互相沟通、磨合，学会与他人合作，共同解决问题，摆脱“一切依托于课堂，课堂依附于老师”的传统教育模式，让学生真正有团队的责任感、使命感和主人翁意识，提升学生的综合能力。</span></p><p><span style="color:#000000;">3）危机处理能力和临场应变能力的提升</span></p><p><span style="color:#000000;">&nbsp;&nbsp;&nbsp;乐队在排练和演出中经常会遇到临场危机，包括演奏失误、设备问题、合作问题等。课程中老师将教会学生如何应对危机，如何巧妙的解决突发状况，这对于学生的问题处理能力、临场应变能力和心理素质的提升均有积极作用。</span></p><p><span style="color:#000000;">4）开发创造力，提升艺术审美和鉴赏力</span></p><p><span style="color:#000000;">&nbsp;&nbsp;&nbsp;老师将指导学生乐队自主编曲、作词，创造属于他们自己的音乐。在创作和改进的过程中，锻炼学生的创造力和想象力，进而提升学生对音乐的整体感知力、鉴赏力和艺术审美。</span></p><p><span style="color:#000000;">5）舞台演出机会丰富履历增强自信心</span></p><p><span style="color:#000000;">&nbsp;&nbsp;&nbsp;RM会为学生乐队提供丰富的舞台表演机会，指导学生参加乐队比赛，让学生在演出和比赛的实际演练中提高技能，增长见识，磨练意志，提升综合素质，增强自信心，同时为有意愿申请国外留学的同学丰富履历。</span></p><p><span style="color:#000000;">6）多语种教学免费语言环境练口语</span></p><p><span style="color:#000000;">&nbsp;&nbsp;&nbsp;RM提供国际化师资团队，教学用语可使用中文、英语、意大利语、西班牙语等，使学生实现音乐与外语的双赢。</span></p><p><br></p>', '<div>&nbsp;The Rolling Music band course is a combination of studying musical instruments,&nbsp;appreciating music and teamwork. It works to improve students'' cooperation and social skills, problem-solving and artistic expression, creativity and self-confidence. This course has been working with international schools, international communities, training centers, study institutions, etc., to create a number of student bands, to participate in large and small performances. It can not only promote the development of students ''physical and mental health, but also explore their capabilities in the field of art, and create an innovative and unique learning experience.</div><div><br></div><div><br></div><div><br></div><div>Course content</div><div><br></div><div>&nbsp; The band teacher will show students how to function as a team in their band and how to communicate through music. Students will learn to appreciate and understand different styles of music, they will learn how to apply this knowledge edge into their own playing, this will help band develop their own unique sound. in addition, students will be taught how to arrange songs, write lyrics, improve their playing skills, communicate on stage, perform with confidence, handle crisis'', respond to on spot mistakes, rehearse productively, promote the band,&nbsp;pose for band pictures, manage the band and so on. The Rolling Music band course is suitable for students who already play an instrument, or an existing band looking for creative or performance counseling, or both.</div><div><br></div><div><br></div><div><br></div><div>Course Advantages</div><div><br></div><div>1) Diverse musical knowledge</div><div><br></div><div>&nbsp; &nbsp;Students will learn through viewing, listening, conversing, etc., about music in a variety of cultures and styles. Students will develop an in-depth understanding of different instruments and their sound, range, and other characteristics. This will allow students to have a general understanding of the many sounds that exist in the world.&nbsp;</div><div><br></div><div>2) Building social skills and making freinds</div><div><br></div><div>&nbsp; &nbsp;RM uses a "band to band" education recess, where bands come together to learn from each other, we use games, composition tasks, group rehearsals, etc. to strengthen the feeling of a team bond to encourage students to communicate with each other, and cooperate to solve problems. This guidance relies solely on the teacher that we provide, "the traditional education model works so that students feel a sense of responsibility as a part of a team, with a goal to enhance the comprehensive ability of each other."</div><div><br></div><div>3) Developing spontaneous problem solving</div><div><br></div><div>&nbsp; Bands often encounter on-spot crises in rehearsals and performances, including performance errors, equipment problems, and cooperation issues. The teacher will teach the students how to deal with these encounters, to come up with an effective solution on the spot which will not only help improve their musicality but also help stimulate their brains to quickly solve problems.</div><div><br></div><div>4) Developing creativity</div><div><br></div><div>&nbsp; &nbsp;The teacher will guide the students in writing their own music and lyric writing. during the writing process the students will exercise their creativity and imagination, and so enhance the students'' overall appreciation and perception of music.</div><div><br></div><div>5) Building self-confidence and musical skills</div><div><br></div><div>&nbsp; &nbsp;RM will provide students with a wealth of stage performances to allow students to present their improvements&nbsp;under the RM band course, students will also be encouraged to participate in band competitions. in performances and competitions, students will exercise and improve their skills, learn more about the music industry, enhance their overall quality, and build self-confidence. Also, it will be appealing on a resume when applying for higher education.&nbsp;</div><div><br></div><div>6) Practicing language through conversation</div><div><br></div><div>&nbsp; &nbsp;RM provides an international team of teachers, teaching language include: Chinese, English, Italian, Spanish, etc., so students can both learn music and practice a foreign language at the same time!</div><div><br></div>', 9, 0, '', '', '', '', '', '', '2016-12-04 00:19:35'),
(10, '音乐理论', 'Music Theory', '<p><br></p><p><img alt="" style="width: 1000px; height: 641px; margin: 5px 10px;" data-cke-saved-src="/uploads/images/9dd354begw1epus83zpa6j20fk0bnabh.jpg" src="/uploads/images/9dd354begw1epus83zpa6j20fk0bnabh.jpg"></p><p><span style="font-family:arial,helvetica,sans-serif;"><strong><span style="color:#ff8c00;"><span style="font-size:18px;">课程介绍</span></span></strong></span></p><p><span style="font-size:16px;"><span style="color:#000000;">&nbsp; &nbsp; 课程结合流行</span></span><span style="font-size:16px;"><span style="color:#000000;">音乐史和不同音乐风格的器乐应用，教授<span style="font-family: arial, 宋体, sans-serif; text-indent: 28px;">五线谱的乐理知识，包括音、音律、记谱法、</span></span></span><span style="font-size:16px;"><span style="color:#000000;"><span style="font-family: arial, 宋体, sans-serif; text-indent: 28px;">调式、音程、和弦、</span></span><span style="color:#000000;"><span style="font-family: arial, 宋体, sans-serif; text-indent: 28px;">节奏、节拍、音乐的速度与力度、转调移调、装饰音和旋律等。</span>适合7岁以上儿童和成人</span></span><span style="font-size: 16px; color: rgb(0, 0, 0);">。</span></p>', '<div>Course Introduction</div><div><br></div><div>&nbsp; &nbsp; &nbsp;The course combines the history of popular music and the instrumental music of different styles. It teaches music theory, including sound, temperament, notation, style, intervals, chords, rhythm, beat, tempo and intensity, transfer tone, decorative tone and melody. Suitable for children and adults over 7 years old.</div>', 10, 0, '', '', '', '', '', '', '2016-12-04 00:20:18');
INSERT INTO `course` (`id`, `name`, `en_name`, `desc`, `en_desc`, `display_order`, `is_top`, `recommand_pic`, `en_recommand_pic`, `pdf`, `en_pdf`, `pdf_name`, `en_pdf_name`, `createtime`) VALUES
(11, '冬夏令营', 'Winter&Summer Camp', '<p><img style="margin-right: 10px; float: left; width: 150px; height: 211px;" data-cke-saved-src="/uploads/images/%E5%A4%8F%E4%BB%A4%E8%90%A5.png" src="/uploads/images/%E5%A4%8F%E4%BB%A4%E8%90%A5.png"><span style="font-size:16px;"><span style="color:#ff8c00;"><strong>课程介绍</strong></span></span></p><p><span style="font-family:arial,helvetica,sans-serif;"><span style="color:#000000;">&nbsp; &nbsp; 每年寒暑推出“Let''s Start A Band小小乐队冬/夏令营”，目的是让大家以最短的时间接触不同的乐器、音乐风格与乐队文化、结识同样喜欢音乐的小伙伴一同玩乐队，形成音乐社交圈，培养学员的团队意识和社交能力。学生将学到吉他、贝斯、鼓、音乐理论、音乐鉴赏及历史课程。专业老师不只会指导大家乐器课，还会帮助大家组成乐队，在闭营时进行演出，享受合奏的乐趣！该课程适合7岁以上儿童及成人，有无器乐基础均可。</span></span></p><p><span style="font-family:arial,helvetica,sans-serif;"><span style="color:#000000;">&nbsp; &nbsp; 近两年，RM已成功开营10余期，期间组成的乐队登上了诸多舞台，如迷笛音乐节“孩子们的迷笛”、上海儿童艺术剧院、碧云音乐季、乐高儿童科技营机械模型发明大赛颁奖礼等。</span></span></p>', '<div>Course Introduction</div><div><br></div><div>&nbsp; &nbsp; &nbsp;Every winter break and summer break we launch our &nbsp;"Let''s Start A Band Winter / Summer Camp", the purpose is to let the participants come in contact with a range of different instruments, music style and band culture&nbsp;in the shortest possible time. The participants are encouraged to socialize with each other and form one big musical community. Participants will learn guitar, bass, drums, music theory, music appreciation and music history. Professional teachers will not only guide the music&nbsp;classes but also help participants form bands. In the end, the participants&nbsp;will present their improvements in a final performance in their own ensembles! This course is suitable for students over the age of 7 and above, including adults.&nbsp;</div><div><br></div><div>&nbsp; &nbsp; &nbsp;Over the past two years, RM has been successful in hosting 10 of these camps. During the camp days the bands played at numeral stages, such as on the &nbsp;"MIDIkids stage" during&nbsp;the Midi Music Festival, Shanghai Children''s Art Theater, BiYun music festival, and the award ceremony for Lego children''s science and technology camp Mechanical Model invention contest.</div>', 11, 1, '/uploads/201702153114kis13RWcSN.jpg', '', '/uploads/201702153114kis13RWcSN.jpg', '', '', '', '2016-12-04 00:21:10');

