CREATE TABLE IF NOT EXISTS `course_config` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `name` varchar(128) NOT NULL COMMENT '内容名',
  `en_name` varchar(128) NOT NULL,
  `teacher_id` int(10) NOT NULL COMMENT '教师id',
  `display_order` int(10) NOT NULL COMMENT '排序',
  `type` varchar(32) NOT NULL COMMENT '类别',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

