CREATE TABLE IF NOT EXISTS `event` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `event_type` tinyint(2) NOT NULL DEFAULT '0' COMMENT '0近期，1往期',
  `name` varchar(128) NOT NULL,
  `en_name` varchar(128) NOT NULL,
  `is_top` tinyint(2) NOT NULL DEFAULT '0',
  `desc` text NOT NULL,
  `en_desc` text NOT NULL,
  `display_order` int(8) NOT NULL,
  `event_img` varchar(512) NOT NULL,
  `createtime` varchar(128) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=20 ;

--
-- 转存表中的数据 `event`
--

INSERT INTO `event` (`id`, `event_type`, `name`, `en_name`, `is_top`, `desc`, `en_desc`, `display_order`, `event_img`, `createtime`) VALUES
(1, 0, '金瀚洋', 'Jin', 0, '<P>鼓手金瀚洋来自建平中学国际部，是RM的第一位学员。年仅16岁的他有着同龄人中鲜见的成熟与内敛，他爱音乐，爱创作，爱帆船......激情澎湃的爱好与他沉稳的气质形成了鲜明的反差。他参加了两期RM夏令营并成功组建2支乐队，先后参加了2015年迷笛音乐节“孩子们的迷笛”演出、2015年碧云家庭日演出、2016年迷笛音乐节“孩子们的迷笛”全国儿童乐队/乐手大赛、2017年迪士尼《疯狂动物城》跨年演出等，2016年夏令营期间创作的原创歌曲《Line Infinity》得到了老师的好评。</P>', '', 1, '/uploads/2017021596717CgR9ojMzP.jpg', '2017-02-15 22:06:47'),
(2, 0, '李其乐', 'Thomas', 0, '<p>“十岁的年龄拥有二十岁的发达头脑”这是吉他老师Giulio对他的评价。广泛的阅读让他学会思考，而天真活泼的性格让他的音乐世界充满童趣与欢乐。在学习吉他期间，他前后参加了2016年碧云国际家庭日演出，精彩的弹唱《加州旅馆》引来无数掌声；2017年，他和他的乐队“机器人”参加了迪士尼“疯狂动物城“跨年演出，一首《The Fox》嗨翻舞台。</p>', '', 2, '/uploads/201702158304qWbwI0VPxg.jpg', '2017-02-15 22:39:44'),
(3, 1, '2017-2-12 RM儿童乐队参加乐高机械模型发明大赛颁奖礼开场表演', '2017-2-12 RM & The Genius Workshop', 0, '<p><span style="FONT-FAMILY: arial, helvetica, sans-serif"><span style="FONT-SIZE: 14px">&nbsp; 2017年2月12日，RM 儿童乐队“猫咪乐队”和冬令营乐队“YKP‘s Rolling Band”和老师们一起来到乐高儿童科技营“2017机械模型发明大赛”颁奖典礼的现场，为现场300多名小发明家们带来精彩炫酷的开场表演！</span></span></p><p><span style="FONT-FAMILY: arial, helvetica, sans-serif"><span style="FONT-SIZE: 14px">&nbsp; 由乐队指导教师Sam、蔡志铮和Loki组成的教师乐队以经典歌曲《Yellow》拉开了颁奖礼的序幕。</span></span></p><p><span style="FONT-FAMILY: arial, helvetica, sans-serif"><span style="FONT-SIZE: 14px"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 361px" alt="" src="/uploads/images/webwxgetmsgimg(2).jpg" data-cke-saved-src="/uploads/images/webwxgetmsgimg(2).jpg"></span></span></p><p><span style="FONT-FAMILY: arial, helvetica, sans-serif"><span style="FONT-SIZE: 14px">&nbsp; 可爱的“猫咪乐队”紧随其后，鼓手吕瑞祁和尤克里里手俞昕远带来了《铃儿响叮当》和《童年》两首好听的歌曲，现场的观众被这两个活泼可爱的小音乐家打动，也跟着打起节拍。猫咪乐队是RM年纪最小的乐队，他们只有6岁，虽然年纪小，但本事可不小哦！这可是他们的第二次演出了哦！</span></span></p><p><span style="FONT-FAMILY: arial, helvetica, sans-serif"><span style="FONT-SIZE: 14px"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 345px" alt="" src="/uploads/images/webwxgetmsgimg(3).jpg" data-cke-saved-src="/uploads/images/webwxgetmsgimg(3).jpg"></span></span></p><p><span style="FONT-FAMILY: arial, helvetica, sans-serif"><span style="FONT-SIZE: 14px">&nbsp; 来自2017RM冬令营的乐队“YKP‘s Rolling Band”凭借Maroon 5 的著名歌曲《Lucky Strike》掀起了现场的高潮。这支乐队由鼓手Harry、键盘手Jenny、吉他手&amp;和声Ethan、贝斯手&amp;和声Lance和主唱QQ组成，他们都是来自包玉刚国际学校的孩子，这是他们第一次组乐队，短短5天的冬令营之后，他们带着自信的笑容走上舞台，为现场观众带来了精彩表演，赞！</span></span></p><p><span style="FONT-FAMILY: arial, helvetica, sans-serif"><span style="FONT-SIZE: 14px"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 279px" alt="" src="/uploads/images/1%20(2).jpg" data-cke-saved-src="/uploads/images/1%20(2).jpg"></span></span></p><p><span style="FONT-FAMILY: arial, helvetica, sans-serif"><span style="FONT-SIZE: 14px">&nbsp; 稳健大气的台风，冷静机智的危机应对，两支小乐队的精彩表现都给大家留下了深刻的印象。</span></span></p><p><br></p><p><br></p>', '<p style="text-align: start; "><span style="font-family: ''Trebuchet MS''; text-align: justify; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;">&nbsp; &nbsp; On February 12th, 2017, the RM children’s band, ”Kitty Band” and a newly formed band from the latest RM Winter Camp, "YKP ''s Rolling Band”, together with the RM Education Team joined the "Mechanical Model Invention Contest Awards Ceremony 2017” in the children’s science and tech camp of Lego. They opened the event with a wonderful performance to more than 300 little inventors!</span></p><p style="text-align: start; "><span style="font-family: ''Trebuchet MS''; text-align: justify; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;">&nbsp; &nbsp; The RM band, featuring RM teachers Sam, Cai Zhizheng and Loki opened with the prelude to the ceremony, the classic song, “Yellow”.</span></p><p><span style="font-family: arial, helvetica, sans-serif;"><span style="font-size: 14px;"><img alt="" src="/uploads/images/webwxgetmsgimg(2).jpg" data-cke-saved-src="/uploads/images/webwxgetmsgimg(2).jpg" style="margin: 5px 320px; width: 500px; height: 361px;"></span></span></p><p style="margin: 0px; text-align: justify; line-height: normal; font-family: ''Trebuchet MS''; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;"><span style="font-kerning: none">&nbsp; &nbsp; The lovely Kitty Band followed the prelude by performing two beautiful songs “Jingle Bells” and “Childhood” played by drummer Lv Ruiqi and ukulele player Yu Xin. Captivated by the two cute little musicians, the audiences followed along with the beat, clapping their hands. Kitty Band is the youngest band of RM. Their average age is only 6. Although small, their&nbsp; confidence is abundant! This is their second show!</span></p><p><span style="font-family: arial, helvetica, sans-serif;"><span style="font-size: 14px;"><img alt="" src="/uploads/images/webwxgetmsgimg(3).jpg" data-cke-saved-src="/uploads/images/webwxgetmsgimg(3).jpg" style="margin: 5px 320px; width: 500px; height: 345px;"></span></span></p><p style="margin: 0px; text-align: justify; line-height: normal; font-family: ''Trebuchet MS''; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;"><span style="font-kerning: none">&nbsp; &nbsp; YKP’s Rolling Band from the winter camp brought a climax to the performance by rocking a version of the famous song, “Lucky Strike” by Maroon 5. This band consists of members Harry (drummer), Jenny (keyboard), Ethan (guitar), Lance (bass and backing vocals), and QQ (vocals). They are all students from Yue-Kong Pao International School. This&nbsp; performance was the first time for them to play a show together. After the short five-day’s experience in the RM Winter Camp, they took to the stage with confident smiles. They have also brought smiles to the audience’s faces with their condifent performances, great!</span></p><p><span style="font-family: arial, helvetica, sans-serif;"><span style="font-size: 14px;"><img alt="" src="/uploads/images/1%20(2).jpg" data-cke-saved-src="/uploads/images/1%20(2).jpg" style="margin: 5px 320px; width: 500px; height: 279px;"></span></span></p><p style="margin: 0px; text-align: justify; line-height: normal; font-family: ''Trebuchet MS''; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;"><span style="font-kerning: none">&nbsp; &nbsp; With their steady and natural performance style, the two little bands impressed the audiences deeply. They showed their wit when facing troubles on the stage.</span></p><div><br></div>', 32, '', '2017-02-15 23:45:54'),
(4, 1, '2017-2-10 小小乐队冬令营闭营啦！', '2017-2-10 Let''s Start a Band and Play a Show Winter Camp', 0, '<p><span style="FONT-FAMILY: arial, helvetica, sans-serif"><span style="FONT-SIZE: 14px">&nbsp; 为期5天的“2017小小乐队冬令营”今天顺利闭营，来自Camp A 和 Camp B 的小朋友们经过了5天的乐队学习和排练，今天，他们向爸爸妈妈和老师们展示他们的学习成果！</span></span></p><p><span style="FONT-FAMILY: arial, helvetica, sans-serif"><span style="FONT-SIZE: 14px"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="/uploads/images/2017-02-10%20154546.jpg" data-cke-saved-src="/uploads/images/2017-02-10%20154546.jpg"></span></span></p><p style="TEXT-ALIGN: center"><span style="FONT-FAMILY: arial, helvetica, sans-serif"><span style="FONT-SIZE: 14px">Camp A 乐队"小贝壳”</span></span></p><p><span style="FONT-FAMILY: arial, helvetica, sans-serif"><span style="FONT-SIZE: 14px"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="/uploads/images/2017-02-10%20185714.jpg" data-cke-saved-src="/uploads/images/2017-02-10%20185714.jpg"></span></span></p><p style="TEXT-ALIGN: center"><span style="FONT-FAMILY: arial, helvetica, sans-serif"><span style="FONT-SIZE: 14px">Camp B乐队“YKP‘s Rolling Band”</span></span></p><p><span style="FONT-FAMILY: arial, helvetica, sans-serif"><span style="FONT-SIZE: 14px">&nbsp; 从团队合作、曲风学习、乐器体验到组成乐队，练习合奏，处理危机......他们从小小的个体凝聚成强大的团队，共同享受音乐。时间虽然很短暂，但这次特别的体验将会成为他们成长道路上意义非凡的一笔。</span></span></p><p><br></p><p><br></p>', '<p style="margin: 0px; text-align: justify; line-height: normal; font-family: ''Trebuchet MS''; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;"><span style="font-kerning: none">&nbsp; &nbsp; The 5-day “Winter Camp” was successfully closed on this day. The children from Camp A and Camp B spent five days learning and rehearsing. On the last day, they show the outcome of the hard work to their parents!</span></p><p><span style="font-family: arial, helvetica, sans-serif;"><span style="font-size: 14px;"><img alt="" src="/uploads/images/2017-02-10%20154546.jpg" data-cke-saved-src="/uploads/images/2017-02-10%20154546.jpg" style="margin: 5px 320px; width: 500px; height: 375px;"></span></span></p><p style="margin: 0px; text-align: justify; line-height: normal; font-family: ''Trebuchet MS''; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;"><span style="font-kerning: none">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;“Little Shell Band”, Camp A</span></p><p><span style="font-family: arial, helvetica, sans-serif;"><span style="font-size: 14px;"><img alt="" src="/uploads/images/2017-02-10%20185714.jpg" data-cke-saved-src="/uploads/images/2017-02-10%20185714.jpg" style="margin: 5px 320px; width: 500px; height: 375px;"></span></span></p><p style="margin: 0px; text-align: justify; line-height: normal; font-family: ''Trebuchet MS''; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;"><span style="font-kerning: none">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;“YKP’s Rolling Band”, Camp B</span></p><p><br></p><p style="margin: 0px; text-align: justify; line-height: normal; font-family: ''Trebuchet MS''; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;"><span style="font-kerning: none">&nbsp; &nbsp; From the teamwork, learning of parts, playing musical instruments to form a band, ensemble practice, dealing with difficulties as a team, each band transformed into a strong team that enjoys music together. Although time was short, this special experience will be a significant part in their growth as musicians and together as bands.</span></p>', 31, '', '2017-02-16 00:52:11'),
(5, 1, '2017-1-17 中智艺术季新年爵士音乐会Larry Graham & Graham Central Station', '2017-1-17 New Year''s Jazz Concert---Larry Graham& Graham Central Station', 0, '<p><span style="DISPLAY: none" data-cke-bookmark="1">&nbsp;</span></p><p><span style="DISPLAY: none" id="cke_bm_436S" data-cke-bookmark="1">&nbsp;</span><span style="COLOR: #000000">&nbsp; &nbsp;2017年1月17日<span style="FONT-FAMILY: arial, helvetica, sans-serif">，Rolling Music热迷音乐邀请世界贝斯大师Larry Graham &amp; Graham Central Station来到上海，参加由上海文广演艺集团与中智上海公司携手共同打造的“中智艺术季”爵士音乐会演出，他们在上海大舞台带来了一场震撼的音乐狂欢。</span></span></p><p><span style="COLOR: #000000"><span style="FONT-FAMILY: arial, helvetica, sans-serif"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 750px" alt="" src="/uploads/images/254875906.jpg" data-cke-saved-src="/uploads/images/254875906.jpg">&nbsp; &nbsp;Larry作为音乐界的典范人物，其所享有的名望和成就可谓是教科书式的。这位大师集谦逊，勤奋，天赋，智慧于一身，很快就成为经验丰富的音乐家跻身于音乐界，并成为七十年代Funk音乐的基石人物。受其音乐影响的大批音乐人和乐迷都是通过乐队Sly and the Family Stone以及Larry创立的Graham Central Station知晓他的。Larry凭借着史诗般的作品和创造性贝斯slap技巧成功跻身于录音乐手行列中，他合作过的艺人数不胜数，而且都是行业的大家，如影响深远的Lenny Kravitz 和伟大的Prince。作为贝斯鼻祖，音乐创作人，Larry在音乐史上深深地烙下自己的印记。</span></span></p><p><span style="COLOR: #000000"><span style="FONT-FAMILY: arial, helvetica, sans-serif"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 308px" alt="" src="/uploads/images/259104998.jpg" data-cke-saved-src="/uploads/images/259104998.jpg">&nbsp;&nbsp;演出现场，热情似火，人们随着音乐一起舞动，气氛十分热烈，所有人都徜徉在现场的音乐当中，和大师一起感受音乐。</span></span></p><p><span style="COLOR: #000000"><span style="FONT-FAMILY: arial, helvetica, sans-serif"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="/uploads/images/1840689685(1).jpg" data-cke-saved-src="/uploads/images/1840689685(1).jpg"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="/uploads/images/75683686(1).jpg" data-cke-saved-src="/uploads/images/75683686(1).jpg"><img style="BORDER-BOTTOM: 0px solid; BORDER-LEFT: 0px solid; MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 331px; BORDER-TOP: 0px solid; BORDER-RIGHT: 0px solid" alt="" src="/uploads/images/1383163523(1).jpg" data-cke-saved-src="/uploads/images/1383163523(1).jpg"></span></span></p><p><span style="COLOR: #000000"><span style="FONT-FAMILY: arial, helvetica, sans-serif"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 668px" alt="" src="/uploads/images/2017-01-18%20021150.jpg" data-cke-saved-src="/uploads/images/2017-01-18%20021150.jpg"></span></span></p><p><span style="COLOR: #000000"><span style="FONT-FAMILY: arial, helvetica, sans-serif">&nbsp; 两个多小时，20首歌曲，Larry和他的乐队为现场的歌迷们带来了一场感动、震撼的视听盛宴。演出结束后，RM与Larry团队举办庆功宴，感谢远道而来的音乐家们，为我们带来了如此精彩的新年音乐会。</span></span></p><p><span style="DISPLAY: none" data-cke-bookmark="1">&nbsp;</span><span style="DISPLAY: none" id="cke_bm_436E" data-cke-bookmark="1">&nbsp;</span><span style="COLOR: #000000"><span microsoft="" sans="" hiragino="" helvetica=""><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="/uploads/images/2017-01-18%20004349.jpg" data-cke-saved-src="/uploads/images/2017-01-18%20004349.jpg"></span></span></p><p><br></p>', '<p><br></p><p style="margin: 0px; text-align: justify; line-height: normal; font-family: ''Trebuchet MS''; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;"><span style="font-kerning: none">&nbsp; &nbsp; On Jan, 17, 2017, RM invited the world famous bass master Larry Graham&nbsp; and his band, Graham Central Station to Shanghai to perform at the jazz concert in the “Zhongzhi Art Fair” held by Shanghai Wenguang Entertainment Group and Zhongzhi Company Shanghai. They brought a stunning musical carnival to the Shanghai Grand Stage.</span></p><p><span style="color: rgb(0, 0, 0);"><span style="font-family: arial, helvetica, sans-serif;"><img alt="" src="/uploads/images/254875906.jpg" data-cke-saved-src="/uploads/images/254875906.jpg" style="margin: 5px 320px; width: 500px; height: 750px;"></span></span></p><p><span style="font-family: ''Trebuchet MS''; text-align: justify; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;">&nbsp; &nbsp; Larry as a model of the music industry, enjoys his fame and achievement like a textbook. The master displays the characteristics of humility, diligence, talent and wisdom. He became an experienced musician very at a rapid speed in the music circle; also, his patented technique became the cornerstone of Funk music in the 70s. A large number of experienced musicians and fans who were influenced by him got to know of him through the band Sly and the Family Stone and Graham Central Station that was set up by Larry. Larry successfully got into the world of music thanks to his epic work and creative slap technique on the bass. He worked with quite a few artists who were very well-known such as far-reaching Lenny Kravitz and&nbsp; the late, great Prince. As a the founder of Slap Bass and music creator, Larry has deeply imprinted his mark on music.</span></p><p><span style="font-family: ''Trebuchet MS''; text-align: justify; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;">&nbsp; &nbsp; The live show was on fire. People danced with the music in the warm atmosphere. All of them were lost in the live music and enjoyed the scene with the master himself.</span><span style="color: rgb(0, 0, 0);"><span style="font-family: arial, helvetica, sans-serif;"><img alt="" src="/uploads/images/259104998.jpg" data-cke-saved-src="/uploads/images/259104998.jpg" style="margin: 5px 320px; width: 500px; height: 308px;"></span></span></p><p><span style="font-family: ''Trebuchet MS''; text-align: justify; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;">&nbsp; &nbsp; The live show was on fire. People danced with the music in the warm atmosphere. All of them were lost in the live music and enjoyed the scene with the master himself.</span></p><p><span style="color: rgb(0, 0, 0);"><span style="font-family: arial, helvetica, sans-serif;"><img alt="" src="/uploads/images/1840689685(1).jpg" data-cke-saved-src="/uploads/images/1840689685(1).jpg" style="margin: 5px 320px; width: 500px; height: 375px;"><img alt="" src="/uploads/images/75683686(1).jpg" data-cke-saved-src="/uploads/images/75683686(1).jpg" style="margin: 5px 320px; width: 500px; height: 375px;"><img alt="" src="/uploads/images/1383163523(1).jpg" data-cke-saved-src="/uploads/images/1383163523(1).jpg" style="border: 0px solid; margin: 5px 320px; width: 500px; height: 331px;"></span></span></p><p><span style="color: rgb(0, 0, 0);"><span style="font-family: arial, helvetica, sans-serif;"><img alt="" src="/uploads/images/2017-01-18%20021150.jpg" data-cke-saved-src="/uploads/images/2017-01-18%20021150.jpg" style="margin: 5px 320px; width: 500px; height: 668px;"></span></span></p><p style="text-align: start; "><span style="text-align: justify; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;"><font color="#000000" face="arial, helvetica, sans-serif">&nbsp; &nbsp;&nbsp;</font></span><span style="font-family: ''Trebuchet MS''; text-align: justify; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;">20 songs shared in more than 2 hours. Larry and his band brought a breathtaking audio/visual feast to the fans. After the show, RM and Larry’s team held a celebration dinner to thank the musicians coming from afar who showed us such a wonderful new year concert.</span></p><p><span style="color: rgb(0, 0, 0);"><img alt="" src="/uploads/images/2017-01-18%20004349.jpg" data-cke-saved-src="/uploads/images/2017-01-18%20004349.jpg" style="margin: 5px 320px; width: 500px; height: 375px;"></span></p><div><br></div>', 30, '', '2017-02-16 01:32:39'),
(6, 1, '2017-1-7 受邀温州正大城，尤克里里公开课来袭', '2017-1-7 Ukulele Master Class in Wenzhou', 0, '<p>&nbsp; 2017年1月7日，RM受邀温州乐清正大城，为当地的孩子们上一节尤克里里公开课。老师带着孩子们做游戏，识乐谱，听节奏，数节拍，画和弦，打手鼓......两个小时的课程，孩子们不但学会了演奏歌曲《幸福拍手歌》，还配合上手鼓、沙锤、摇铃，完成了现场大合奏！</p><p><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="/uploads/images/2017-01-07%20155248.jpg" data-cke-saved-src="/uploads/images/2017-01-07%20155248.jpg"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="/uploads/images/2017-01-07%20143253.jpg" data-cke-saved-src="/uploads/images/2017-01-07%20143253.jpg"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="/uploads/images/2017-01-07%20150018.jpg" data-cke-saved-src="/uploads/images/2017-01-07%20150018.jpg"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="/uploads/images/2017-01-07%20155234.jpg" data-cke-saved-src="/uploads/images/2017-01-07%20155234.jpg"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="/uploads/images/2017-01-07%20155234.jpg" data-cke-saved-src="/uploads/images/2017-01-07%20155234.jpg">​</p>', '<p style="margin: 0px; text-align: justify; line-height: normal; font-family: ''Trebuchet MS''; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;"><span style="font-kerning: none">&nbsp; &nbsp; On January 7th, 2017, RM was invited to Le Qing Zheng Da Cheng, Wenzhou to give a Ukulele master class. Teachers played games with the children, and taught them to read chord symbols, listen to rhythm, count beats, write chord symbols, play the tambourin and much more. The whole course lasted for two hours. The kids not only learnt to play “The Happy Song” with Ukulele, but also played tambourine, cabasa and bells to create a live ensemble.</span></p><p style="margin: 0px; text-align: justify; line-height: normal; font-family: ''Trebuchet MS''; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;"><span style="font-kerning: none">​</span><br></p><p><img alt="" src="/uploads/images/2017-01-07%20155248.jpg" data-cke-saved-src="/uploads/images/2017-01-07%20155248.jpg" style="margin: 5px 320px; width: 500px; height: 375px;"><img alt="" src="/uploads/images/2017-01-07%20143253.jpg" data-cke-saved-src="/uploads/images/2017-01-07%20143253.jpg" style="margin: 5px 320px; width: 500px; height: 375px;"><img alt="" src="/uploads/images/2017-01-07%20150018.jpg" data-cke-saved-src="/uploads/images/2017-01-07%20150018.jpg" style="margin: 5px 320px; width: 500px; height: 375px;"><img alt="" src="/uploads/images/2017-01-07%20155234.jpg" data-cke-saved-src="/uploads/images/2017-01-07%20155234.jpg" style="margin: 5px 320px; width: 500px; height: 375px;"><img alt="" src="/uploads/images/2017-01-07%20155234.jpg" data-cke-saved-src="/uploads/images/2017-01-07%20155234.jpg" style="margin: 5px 320px; width: 500px; height: 375px;">​</p>', 28, '', '2017-02-16 01:50:42'),
(7, 1, '2017-1-1 虹桥南丰城迪士尼“疯狂动物城”跨年演出', 'Disney “Zootopia” Countdown Party in Nanfeng, Hongqiao', 0, '<p><span style="COLOR: #000000"><span style="FONT-SIZE: 16px"><span style="FONT-FAMILY: arial, helvetica, sans-serif">&nbsp; 2016年12月31日--2017年1月1日，RM学员乐队受邀虹桥南丰城迪士尼“疯狂动物城”跨年演出。<span style="TEXT-ALIGN: justify; LETTER-SPACING: 2px">经过</span><span style="BOX-SIZING: border-box; TEXT-ALIGN: justify; PADDING-BOTTOM: 0px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; MAX-WIDTH: 100%; WORD-WRAP: break-word !important; LETTER-SPACING: 2px; PADDING-TOP: 0px">面试，考核，乐队集训</span><span style="TEXT-ALIGN: justify; LETTER-SPACING: 2px">，最终有4支学生乐队</span><span style="BOX-SIZING: border-box; TEXT-ALIGN: justify; PADDING-BOTTOM: 0px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; MAX-WIDTH: 100%; WORD-WRAP: break-word !important; LETTER-SPACING: 2px; PADDING-TOP: 0px">登上</span><span style="TEXT-ALIGN: justify; LETTER-SPACING: 2px">“疯狂动物城”的</span><span style="BOX-SIZING: border-box; TEXT-ALIGN: justify; PADDING-BOTTOM: 0px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; MAX-WIDTH: 100%; WORD-WRAP: break-word !important; LETTER-SPACING: 2px; PADDING-TOP: 0px">舞台</span><span style="TEXT-ALIGN: justify; LETTER-SPACING: 2px">。这些孩子中有沉稳安静的气质少年，有热情奔放的热血男孩、有飒爽霸气的窈窕少女、有清新可爱的邻家姑娘，还有古灵精怪的民谣小子......不一样的风格让舞台更加缤纷多彩。</span></span></span></span></p><p><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 331px" alt="" src="/uploads/images/DSC_0423.JPG" data-cke-saved-src="/uploads/images/DSC_0423.JPG"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 331px" alt="" src="/uploads/images/DSC_0481.JPG" data-cke-saved-src="/uploads/images/DSC_0481.JPG"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 331px" alt="" src="/uploads/images/DSC_0574.JPG" data-cke-saved-src="/uploads/images/DSC_0574.JPG"></p><p><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 331px" alt="" src="/uploads/images/DSC_0582.JPG" data-cke-saved-src="/uploads/images/DSC_0582.JPG">​<img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 331px" alt="" src="/uploads/images/DSC_0530.JPG" data-cke-saved-src="/uploads/images/DSC_0530.JPG"></p>', '<p><br></p><p style="margin: 0px; text-align: justify; line-height: normal; font-family: ''Trebuchet MS''; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;"><span style="font-kerning: none">&nbsp; &nbsp; From Dec 31st 2016 to Jan 1st, 2017, the students from RM were invited to give performances at the Disney “Zootopia” Countdown Party in Nanfeng, Hongqiao. After the audition, examinations and band training, there were four bands that finally performed on the stage. Some of the kids were calm and quiet; some were crazy and excitable boys; others were valiant and graceful girls; others were lovely and pretty; all displayed in different styles that made the show colourful.</span></p><p style="margin: 0px; text-align: justify; line-height: normal; font-family: ''Trebuchet MS''; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;"><span style="font-kerning: none">​</span><br></p><p><img alt="" src="/uploads/images/DSC_0423.JPG" data-cke-saved-src="/uploads/images/DSC_0423.JPG" style="margin: 5px 320px; width: 500px; height: 331px;"><img alt="" src="/uploads/images/DSC_0481.JPG" data-cke-saved-src="/uploads/images/DSC_0481.JPG" style="margin: 5px 320px; width: 500px; height: 331px;"><img alt="" src="/uploads/images/DSC_0574.JPG" data-cke-saved-src="/uploads/images/DSC_0574.JPG" style="margin: 5px 320px; width: 500px; height: 331px;"></p><p><img alt="" src="/uploads/images/DSC_0582.JPG" data-cke-saved-src="/uploads/images/DSC_0582.JPG" style="margin: 5px 320px; width: 500px; height: 331px;">​<img alt="" src="/uploads/images/DSC_0530.JPG" data-cke-saved-src="/uploads/images/DSC_0530.JPG" style="margin: 5px 320px; width: 500px; height: 331px;"></p>', 27, '', '2017-02-16 02:20:06'),
(8, 1, '2017-1-13 进才国际学校期末汇报演出', '2017-1-13 Jincai International School ASP', 0, '<p><span style="FONT-FAMILY: arial, helvetica, sans-serif"><span style="FONT-SIZE: 16px"><span style="COLOR: #000000">&nbsp; 2017年1月13日，进才国际学校小学部迎来了一学期一次的ASP学期末汇报演出。这一次，RM共有3支学生乐队登上舞台。</span></span></span></p><p><span style="FONT-FAMILY: arial, helvetica, sans-serif"><span style="FONT-SIZE: 16px"><span style="COLOR: #000000">&nbsp; 吉他手Mighty带来弹唱表演，鼓手Harin Yoon和Jack带来了架子鼓合奏，而鼓手Mavin和吉他手Kangyu再一次成为最帅的乐队，掀起了全场的高潮。</span></span></span></p><p><span style="FONT-FAMILY: arial, helvetica, sans-serif"><span style="FONT-SIZE: 16px"><span style="COLOR: #000000"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="/uploads/images/2017-01-11%20162943.jpg" data-cke-saved-src="/uploads/images/2017-01-11%20162943.jpg"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="/uploads/images/2017-01-11%20163502.jpg" data-cke-saved-src="/uploads/images/2017-01-11%20163502.jpg"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="/uploads/images/2017-01-11%20163749.jpg" data-cke-saved-src="/uploads/images/2017-01-11%20163749.jpg">​</span></span></span></p>', '<p><span style="font-family: ''Trebuchet MS''; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;">&nbsp; &nbsp; On January 13th, 2017, the primary section of JinCai International School held the once-a-term ASP concert at the end of the school semester. This time, three bands from RM performed on the stage.</span><br></p><p><span style="font-family: ''Trebuchet MS''; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;">​ &nbsp; &nbsp;</span><span style="font-family: ''Trebuchet MS''; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;">The guitar player Mighty sang while playing guitar. Drummers Harin Yoon and Jack played a dual drum solo. The drummer Marvin and the guitarist Kangyu became the coolest band once again and brought a climax to the show.</span></p><p><span style="font-family: arial, helvetica, sans-serif;"><span style="font-size: 16px;"><span style="color: rgb(0, 0, 0);"><img alt="" src="/uploads/images/2017-01-11%20162943.jpg" data-cke-saved-src="/uploads/images/2017-01-11%20162943.jpg" style="margin: 5px 320px; width: 500px; height: 375px;"><img alt="" src="/uploads/images/2017-01-11%20163502.jpg" data-cke-saved-src="/uploads/images/2017-01-11%20163502.jpg" style="margin: 5px 320px; width: 500px; height: 375px;"><img alt="" src="/uploads/images/2017-01-11%20163749.jpg" data-cke-saved-src="/uploads/images/2017-01-11%20163749.jpg" style="margin: 5px 320px; width: 500px; height: 375px;">​</span></span></span></p>', 29, '', '2017-02-16 02:36:45'),
(9, 1, '2016-10-23 南京历史文化名城博览会开幕式', '2016-10-23 World Historical & Cultural Cities Expo', 0, '<p><span style="FONT-FAMILY: arial, helvetica, sans-serif"><span style="COLOR: #000000"><span style="TEXT-ALIGN: justify; FONT-SIZE: 14px">&nbsp; 2016年10月23日，南京历史文化名城博览会开幕式在南京中华门城堡举行，向来自世界各地的朋友们展现中华传统农文化的魅力。Rolling Music热迷音乐非常荣幸的担任名城会开幕式总策划。</span></span></span></p><p><span style="FONT-FAMILY: arial, helvetica, sans-serif"><span style="COLOR: #000000"><span style="TEXT-ALIGN: justify; FONT-SIZE: 14px">&nbsp; 我们将以往开幕式的宏大叙事之风貌提升到极简主义的风格，</span><span style="TEXT-ALIGN: justify; FONT-SIZE: 14px">采用</span><span style="BOX-SIZING: border-box; TEXT-ALIGN: justify; PADDING-BOTTOM: 0px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; MAX-WIDTH: 100%; WORD-WRAP: break-word !important; FONT-SIZE: 14px; PADDING-TOP: 0px">沉浸式剧场、3D灯光技术，将文化遗产和时尚科技完美融合</span><span style="BOX-SIZING: border-box; TEXT-ALIGN: justify; PADDING-BOTTOM: 0px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; MAX-WIDTH: 100%; WORD-WRAP: break-word !important; FONT-SIZE: 14px; PADDING-TOP: 0px">，打造一场炫丽的视觉盛宴</span><span style="TEXT-ALIGN: justify; FONT-SIZE: 14px">，向世界展示一带一路节点城市——人文绿都南京的厚重积淀、不懈追求与辉煌愿景，</span><span style="TEXT-ALIGN: justify; FONT-SIZE: 14px">充分表达传统文化的美学与诗意及其与当代美学的融汇。</span></span></span></p><p><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 333px" alt="" src="/uploads/images/IMG_6605.jpg" data-cke-saved-src="/uploads/images/IMG_6605.jpg"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 334px" alt="" src="/uploads/images/IMG_1406.jpg" data-cke-saved-src="/uploads/images/IMG_1406.jpg"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 334px" alt="" src="/uploads/images/IMG_1441.jpg" data-cke-saved-src="/uploads/images/IMG_1441.jpg"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 334px" alt="" src="/uploads/images/IMG_6746.jpg" data-cke-saved-src="/uploads/images/IMG_6746.jpg"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 334px" alt="" src="/uploads/images/IMG_6716.jpg" data-cke-saved-src="/uploads/images/IMG_6716.jpg">&nbsp;&nbsp;<span style="COLOR: #000000">开幕式受到了社会各界的广泛关注，中央电视台《新闻联播》、南京电视台、《人民日报》、《南京晨报》等媒体纷纷进行报道。</span></p><p><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 343px" alt="" src="/uploads/images/%E6%96%B0%E9%97%BB%E8%81%94%E6%92%AD%E5%AF%B9%E5%90%8D%E5%9F%8E%E4%BC%9A%E7%9A%84%E6%8A%A5%E9%81%93.png" data-cke-saved-src="/uploads/images/%E6%96%B0%E9%97%BB%E8%81%94%E6%92%AD%E5%AF%B9%E5%90%8D%E5%9F%8E%E4%BC%9A%E7%9A%84%E6%8A%A5%E9%81%93.png"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 889px" alt="" src="/uploads/images/411904762019323611.jpg" data-cke-saved-src="/uploads/images/411904762019323611.jpg"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 667px" alt="" src="/uploads/images/215962211240915162.jpg" data-cke-saved-src="/uploads/images/215962211240915162.jpg"></p>', '<p style="margin: 0px; text-align: justify; line-height: normal; font-family: ''Trebuchet MS''; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;"><span style="font-kerning: none">&nbsp; &nbsp; On October 23rd, 2016, the opening ceremony of Nanjing Famous Historical Cultural City Expo was held at Zhonghua Gate Castle in Nanjing. It showed the charm of Chinese traditional culture to the world. It was RM’s honor to organize this opening ceremony.</span></p><p style="margin: 0px; text-align: justify; line-height: normal; font-family: ''Trebuchet MS''; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;"><span style="font-kerning: none"></span><br></p><p style="margin: 0px; text-align: justify; line-height: normal; font-family: ''Trebuchet MS''; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;"><span style="font-kerning: none">&nbsp; &nbsp; We turned the former grand narrative style ceremony into a simple style. Using immersive theatre and 3D lighting technology to combine cultural heritage and the latest technology. We created a dazzling visual feast to show the accumulation, relentless pursuit and brilliant vision of Nanjing which is the joint city of One Belt, One Road. The opening ceremony conveyed the combined beauty of traditional culture, poetic sentiment and contemporary aesthetic.&nbsp;</span></p><div><br></div><p><img alt="" src="/uploads/images/IMG_6605.jpg" data-cke-saved-src="/uploads/images/IMG_6605.jpg" style="margin: 5px 320px; width: 500px; height: 333px;"><img alt="" src="/uploads/images/IMG_1406.jpg" data-cke-saved-src="/uploads/images/IMG_1406.jpg" style="margin: 5px 320px; width: 500px; height: 334px;"><img alt="" src="/uploads/images/IMG_1441.jpg" data-cke-saved-src="/uploads/images/IMG_1441.jpg" style="margin: 5px 320px; width: 500px; height: 334px;"><img alt="" src="/uploads/images/IMG_6746.jpg" data-cke-saved-src="/uploads/images/IMG_6746.jpg" style="margin: 5px 320px; width: 500px; height: 334px;"><img alt="" src="/uploads/images/IMG_6716.jpg" data-cke-saved-src="/uploads/images/IMG_6716.jpg" style="margin: 5px 320px; width: 500px; height: 334px;"><br></p><p>&nbsp; &nbsp; &nbsp;<span style="font-family: ''Trebuchet MS''; text-align: justify; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;">The opening ceremony attracted considerable attention from all social circles. CCTV News, Nanjing TV Station, People’s Daily and Nanjing Morning News and other medias that reported this ceremony respectively.&nbsp;</span></p><p><img alt="" src="/uploads/images/%E6%96%B0%E9%97%BB%E8%81%94%E6%92%AD%E5%AF%B9%E5%90%8D%E5%9F%8E%E4%BC%9A%E7%9A%84%E6%8A%A5%E9%81%93.png" data-cke-saved-src="/uploads/images/%E6%96%B0%E9%97%BB%E8%81%94%E6%92%AD%E5%AF%B9%E5%90%8D%E5%9F%8E%E4%BC%9A%E7%9A%84%E6%8A%A5%E9%81%93.png" style="margin: 5px 320px; width: 500px; height: 343px;"><img alt="" src="/uploads/images/411904762019323611.jpg" data-cke-saved-src="/uploads/images/411904762019323611.jpg" style="margin: 5px 320px; width: 500px; height: 889px;"><img alt="" src="/uploads/images/215962211240915162.jpg" data-cke-saved-src="/uploads/images/215962211240915162.jpg" style="margin: 5px 320px; width: 500px; height: 667px;"></p>', 26, '', '2017-02-16 03:07:37'),
(10, 1, '2016-9-25 RM老师亮相河马生活节', '2016-9-25 Shanghai Wine & Dine Festival', 0, '<p><span style="COLOR: #000000">&nbsp; 2016年9月23日、24日、25日，上海世博公园举办了盛大的“河马生活节”！这是美食，美酒，美景共享的嘉年华，现场有来自数十个国家的1000多种美酒和2500多种美食，吸引了大批“吃货”。</span></p><p><span style="COLOR: #000000"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 281px" alt="" src="/uploads/images/1154348200.jpg" data-cke-saved-src="/uploads/images/1154348200.jpg">&nbsp;&nbsp;夜幕降临，RM的老师们闪亮登场，点亮河畔舞台，开启精彩的音乐狂欢。河马生活节河畔舞台的音乐演出由RM策划执行，RM电音&amp;贝斯老师Giulio、乐队指导&amp;架子鼓老师Sam、乐队指导&amp;吉他老师James、键盘老师叶晔、贝斯老师刘炜等纷纷登台亮相，现场高潮迭起，粉丝热情洋溢，节目精彩纷呈。</span></p><p><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 334px" alt="" src="/uploads/images/2S0A3515.JPG" data-cke-saved-src="/uploads/images/2S0A3515.JPG"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 334px" alt="" src="/uploads/images/2S0A4801.JPG" data-cke-saved-src="/uploads/images/2S0A4801.JPG"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 334px" alt="" src="/uploads/images/2S0A3520%20-%20%E5%89%AF%E6%9C%AC.JPG" data-cke-saved-src="/uploads/images/2S0A3520%20-%20%E5%89%AF%E6%9C%AC.JPG"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 333px" alt="" src="/uploads/images/2S0A4809.JPG" data-cke-saved-src="/uploads/images/2S0A4809.JPG"><img style="MARGIN: 5px 320px; WIDTH: 500px; HEIGHT: 334px" alt="" src="/uploads/images/2S0A4852.JPG" data-cke-saved-src="/uploads/images/2S0A4852.JPG"></p>', '<p></p><p style="margin: 0px; text-align: justify; line-height: normal; font-family: ''Trebuchet MS''; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;"><span style="font-kerning: none">&nbsp; &nbsp; The grand “Wine &amp; Dine Festival” (Hippo Life Festival) was held at the Shanghai Expo Park from September 23rd to the 25th, 2016. This was a carnival celebrating the sharing of food, wine and fine dining. It was attractive to the people who loved eating. There were more than 1000 kinds of good wines and more than 2500 kinds of delicate foods from dozens of countries.</span></p><p><span style="color: rgb(0, 0, 0);"><img alt="" src="/uploads/images/1154348200.jpg" data-cke-saved-src="/uploads/images/1154348200.jpg" style="margin: 5px 320px; width: 500px; height: 281px;"></span></p><p><span style="color: rgb(0, 0, 0);"></span><span style="font-family: ''Trebuchet MS''; text-align: justify; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;">&nbsp; &nbsp; Evening fell. The carnival began with the great appearance of the teachers from RM on the Riverside Stage. The show on Riverside Stage was organized by RM. Giulio (DJ &amp; bass teacher), Sam (band director &amp; drum teacher), James (band director &amp; guitar teacher), Ye Hua (keyboard teacher), Liu Ye (bass teacher), etc. They gave performances on the stage. The performance had the audience in rapture time and time again, while the shows were brilliant and the fans were full of enthusiasm.</span></p><p><img alt="" src="/uploads/images/2S0A3515.JPG" data-cke-saved-src="/uploads/images/2S0A3515.JPG" style="margin: 5px 320px; width: 500px; height: 334px;"><img alt="" src="/uploads/images/2S0A4801.JPG" data-cke-saved-src="/uploads/images/2S0A4801.JPG" style="margin: 5px 320px; width: 500px; height: 334px;"><img alt="" src="/uploads/images/2S0A3520%20-%20%E5%89%AF%E6%9C%AC.JPG" data-cke-saved-src="/uploads/images/2S0A3520%20-%20%E5%89%AF%E6%9C%AC.JPG" style="margin: 5px 320px; width: 500px; height: 334px;"><img alt="" src="/uploads/images/2S0A4809.JPG" data-cke-saved-src="/uploads/images/2S0A4809.JPG" style="margin: 5px 320px; width: 500px; height: 333px;"><img alt="" src="/uploads/images/2S0A4852.JPG" data-cke-saved-src="/uploads/images/2S0A4852.JPG" style="margin: 5px 320px; width: 500px; height: 334px;"></p>', 25, '', '2017-02-16 03:39:45'),
(11, 1, '2015-6-14 与碧云社区联合举办碧云音乐季', '2015-6-14 Rolling Music& Bi Yun Community hold Bi Yun Music Season', 0, '<P><SPAN style="FONT-SIZE: 16px"><SPAN style="FONT-FAMILY: arial, helvetica, sans-serif">&nbsp;&nbsp; &nbsp;Rolling Music带着来自7个不同国家不同风格的乐队，为碧云音乐季带来了一场精彩纷呈的演出。 我们的学校老师带着自己的学生组成的乐队，给现场的家长和孩子亲切有趣的演出。当音乐响起，现场的老师带着可爱的孩子们一起在音乐中起舞。</SPAN></SPAN></P>\r\n<P data-cke-expando="undefined"><SPAN style="FONT-SIZE: 16px" data-cke-expando="undefined"><SPAN style="FONT-FAMILY: arial, helvetica, sans-serif" data-cke-expando="undefined"><IMG style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="/uploads/images/%E5%B0%81%E9%9D%A2%E5%9B%BE.jpg" data-cke-saved-src="/uploads/images/%E5%B0%81%E9%9D%A2%E5%9B%BE.jpg"><IMG style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="/uploads/images/066ed1649eaa31dae728d3b15c726381-sz_236357.jpg" data-cke-saved-src="/uploads/images/066ed1649eaa31dae728d3b15c726381-sz_236357.jpg"><IMG style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 666px" alt="" src="/uploads/images/83fc6ef05aaa98affa02abc31b15c6aa-sz_154640.jpg" data-cke-saved-src="/uploads/images/83fc6ef05aaa98affa02abc31b15c6aa-sz_154640.jpg"><IMG style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="/uploads/images/logo.jpg" data-cke-saved-src="/uploads/images/logo.jpg"></SPAN></SPAN></P>', '<P><SPAN style="FONT-FAMILY: arial, helvetica, sans-serif"><SPAN style="FONT-SIZE: 16px">&nbsp;&nbsp;&nbsp; Rolling Music brought Seven bands from different countries brought a spectacular performance, the mentors from Rolling Music School, together with their students from the 2015 Rock Band Summer Camp gave a enjoyable performances for all the audience. There are a lot of talented producers, musicians and renowned bands, such like the jazz band LYZ build up by the mentors from Rolling Music, the world band RTM that has performed on different music festival, the teenage band '' From Somewhere else'' which just won the best rock band in Mercedes-Benz Arena, and Rhetoric\\Shut down\\Unbalanced from Korea that plays Hiphop, Rock and other styles in fusion.</SPAN></SPAN></P>\r\n<P data-cke-expando="undefined"><SPAN style="FONT-FAMILY: arial, helvetica, sans-serif" data-cke-expando="undefined"><SPAN style="FONT-SIZE: 16px" data-cke-expando="undefined"><IMG style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="http://www.rollingmusic.cn/uploads/images/logo.jpg" data-cke-saved-src="/uploads/images/logo.jpg"></SPAN></SPAN></P>', 1, '', '2017-02-18 22:36:22'),
(12, 1, '2015-6-29 RM举办第一次公开课', '2015-6-29 Open Class', 0, '<P><SPAN style="FONT-SIZE: 16px"><SPAN style="FONT-FAMILY: arial, helvetica, sans-serif">&nbsp;&nbsp;&nbsp; 2015年6月29日，第一堂公开课举办，有近20各家庭参加了这次公开课。孩子们尝试了各种乐器，还举行了一次速成体验后的演出。</SPAN></SPAN></P>\r\n<P data-cke-expando="undefined"><SPAN style="FONT-SIZE: 16px" data-cke-expando="undefined"><SPAN style="FONT-FAMILY: arial, helvetica, sans-serif" data-cke-expando="undefined"><IMG style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 333px" alt="" src="/uploads/images/0709_2.jpg" data-cke-saved-src="/uploads/images/0709_2.jpg"><IMG style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 333px" alt="" src="/uploads/images/0709_3.jpg" data-cke-saved-src="/uploads/images/0709_3.jpg"><IMG style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 333px" alt="" src="/uploads/images/0709_6.jpg" data-cke-saved-src="/uploads/images/0709_6.jpg"><IMG style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 333px" alt="" src="/uploads/images/%E5%B0%81%E9%9D%A2%E5%9B%BE%20(1).jpg" data-cke-saved-src="/uploads/images/%E5%B0%81%E9%9D%A2%E5%9B%BE%20(1).jpg"></SPAN></SPAN></P>', '<P><SPAN style="FONT-SIZE: 16px">29<SUP>th</SUP>&nbsp;June 2015 &nbsp;Open Class</SPAN></P>\r\n<P><SPAN style="FONT-SIZE: 16px">&nbsp;in THE HUB</SPAN></P>\r\n<P><SPAN style="FONT-SIZE: 16px">First open class, more than 20 families had joined this fun music class.</SPAN></P>\r\n<P data-cke-expando="undefined"><SPAN style="FONT-SIZE: 16px" data-cke-expando="undefined">&nbsp;</P>\r\n<P data-cke-expando="undefined"><SPAN style="FONT-SIZE: 16px" data-cke-expando="undefined"><SPAN style="FONT-FAMILY: arial, helvetica, sans-serif" data-cke-expando="undefined"><IMG style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 333px" alt="" src="http://www.rollingmusic.cn/uploads/images/0709_2.jpg" data-cke-saved-src="/uploads/images/0709_2.jpg"><IMG style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 333px" alt="" src="http://www.rollingmusic.cn/uploads/images/0709_3.jpg" data-cke-saved-src="/uploads/images/0709_3.jpg"><IMG style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 333px" alt="" src="http://www.rollingmusic.cn/uploads/images/0709_6.jpg" data-cke-saved-src="/uploads/images/0709_6.jpg"><IMG style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 333px" alt="" src="http://www.rollingmusic.cn/uploads/images/%E5%B0%81%E9%9D%A2%E5%9B%BE%20(1).jpg" data-cke-saved-src="/uploads/images/%E5%B0%81%E9%9D%A2%E5%9B%BE%20(1).jpg"></SPAN></SPAN></P></SPAN>', 2, '', '2017-02-18 22:45:44'),
(13, 1, '2015-7-4 RM乐队参加美国国庆日演出', '2015-7-4   Independence Day of  America', 0, '<p><span style="FONT-SIZE: 16px">&nbsp; 2015年7月4日，在美国国庆日当天，Rolling Music接受了美商会的邀请，参与了美国国庆日的活动。Rolling Music 导师带着学生一起，将美好的音乐带到了美国国庆日的现场。</span></p><p data-cke-expando="undefined"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="/uploads/images/1009939944_meitu_2.jpg" data-cke-saved-src="/uploads/images/1009939944_meitu_2.jpg"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="/uploads/images/2077789823.jpg" data-cke-saved-src="/uploads/images/2077789823.jpg"></p>', '<p><span style="FONT-SIZE: 16px">&nbsp;&nbsp; &nbsp;4<sup>th</sup>&nbsp;July 2015 Independence Day of &nbsp;America, Camp teacher&amp; student performed on the stage</span></p><p><span style="FONT-SIZE: 16px">&nbsp;&nbsp;&nbsp; On 4th July, Independence Day of America, Rolling music student played with &nbsp;band &nbsp;LYZ leading by Ye Ye, who is Rolling music’s teacher, had performed on stage at American Independence Day Party hosted by Amcham. &nbsp;Bravo!&nbsp;</span></p><p data-cke-expando="undefined"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="http://www.rollingmusic.cn/uploads/images/1009939944_meitu_2.jpg" data-cke-saved-src="/uploads/images/1009939944_meitu_2.jpg"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="http://www.rollingmusic.cn/uploads/images/2077789823.jpg" data-cke-saved-src="/uploads/images/2077789823.jpg"></p><p><br></p>', 3, '', '2017-02-18 22:54:01');
INSERT INTO `event` (`id`, `event_type`, `name`, `en_name`, `is_top`, `desc`, `en_desc`, `display_order`, `event_img`, `createtime`) VALUES
(14, 1, '2015-7-10  第一期学生Camp A 结束汇报演出', '2015-7-10  Camp A Rock Band performance', 0, '<p><span style="FONT-SIZE: 16px">&nbsp; 2015年7月10日，非常值得纪念的一天，Rolling Music 第一期的学员顺利结束了学习。我们在这天进行了汇报演出，他们用仅仅两周的时间带来了3首完整演绎的曲子，还即兴为过生日的家长和老师演奏了生日快乐歌。欢乐是最确切的形容词。</span></p><p data-cke-expando="undefined"><span style="FONT-SIZE: 16px" data-cke-expando="undefined"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="/uploads/images/%E5%B0%81%E9%9D%A2%E5%9B%BE(1).jpg" data-cke-saved-src="/uploads/images/%E5%B0%81%E9%9D%A2%E5%9B%BE(1).jpg"></span></p><p data-cke-expando="undefined"><span style="FONT-SIZE: 16px" data-cke-expando="undefined"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 667px" alt="" src="/uploads/images/1230_6.jpg" data-cke-saved-src="/uploads/images/1230_6.jpg"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 667px" alt="" src="/uploads/images/1230_7.jpg" data-cke-saved-src="/uploads/images/1230_7.jpg"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="/uploads/images/1230_4.jpg" data-cke-saved-src="/uploads/images/1230_4.jpg"></span></p><p><br></p>', '<p><span style="FONT-SIZE: 16px">10<sup>th</sup>&nbsp;July 2015 Camp A Rock Band performance</span></p><p><span style="FONT-SIZE: 16px">&nbsp; &nbsp; July 10th, Rolling Music Camp A students has put on a successful performance. It only took 2 weeks for our students to play 3 songs in the band and gave this 30mins concert started from scratch.&nbsp;</span></p><p data-cke-expando="undefined"><span style="FONT-SIZE: 16px"><span style="FONT-SIZE: 16px" data-cke-expando="undefined"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="http://www.rollingmusic.cn/uploads/images/%E5%B0%81%E9%9D%A2%E5%9B%BE(1).jpg" data-cke-saved-src="/uploads/images/%E5%B0%81%E9%9D%A2%E5%9B%BE(1).jpg"></span></span></p><p data-cke-expando="undefined"><span style="FONT-SIZE: 16px"><span style="FONT-SIZE: 16px" data-cke-expando="undefined"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 667px" alt="" src="http://www.rollingmusic.cn/uploads/images/1230_6.jpg" data-cke-saved-src="/uploads/images/1230_6.jpg"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 667px" alt="" src="http://www.rollingmusic.cn/uploads/images/1230_7.jpg" data-cke-saved-src="/uploads/images/1230_7.jpg"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="http://www.rollingmusic.cn/uploads/images/1230_4.jpg" data-cke-saved-src="/uploads/images/1230_4.jpg"></span></span></p>', 4, '', '2017-02-18 23:05:16'),
(15, 1, '2015-7-25 第一支Rolling Music Band 在上海儿童艺术剧院进行演出', '2015-7-25 Rolling music band show at children’s  theatre', 0, '<p><span style="FONT-SIZE: 16px">&nbsp;&nbsp;&nbsp; 2015年</span><span style="FONT-SIZE: 16px">7月25日，Rolling Music受邀参加在上海儿童艺术剧院正在演出的著名加拿大儿童剧《饥饿的毛毛虫》的暖场演出。这也是Rolling Music学员Bruce和King 的第一次正式演出，和他们的老师 Cole（音乐作曲系博士）一起，在舞台上展示学习的成果。</span></p><p><br></p><p><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 340px" alt="" src="/uploads/images/%E5%B0%81%E9%9D%A2%E5%9B%BE%20(1)(1).jpg" data-cke-saved-src="/uploads/images/%E5%B0%81%E9%9D%A2%E5%9B%BE%20(1)(1).jpg"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 500px" alt="" src="/uploads/images/1230_3.jpg" data-cke-saved-src="/uploads/images/1230_3.jpg"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 500px" alt="" src="/uploads/images/1230_7%20(1).jpg" data-cke-saved-src="/uploads/images/1230_7%20(1).jpg"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 500px" alt="" src="/uploads/images/1230_5.jpg" data-cke-saved-src="/uploads/images/1230_5.jpg"></p>', '<p><span style="FONT-SIZE: 16px">25<sup>th</sup>&nbsp;July 2015 Rolling music band show at children’s &nbsp;theatre</span></p><p><br></p><p><span style="FONT-SIZE: 16px">&nbsp; &nbsp; On 25<sup>th</sup>&nbsp;July Rolling music Camp A band was invited by Shanghai Children’s theatre to perform at the opening show of famous Canada drama <the very="" hungry="" p=""> </the></span></p><p><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 340px" alt="" src="http://www.rollingmusic.cn/uploads/images/%E5%B0%81%E9%9D%A2%E5%9B%BE%20(1)(1).jpg" data-cke-saved-src="/uploads/images/%E5%B0%81%E9%9D%A2%E5%9B%BE%20(1)(1).jpg"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 500px" alt="" src="http://www.rollingmusic.cn/uploads/images/1230_3.jpg" data-cke-saved-src="/uploads/images/1230_3.jpg"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 500px" alt="" src="http://www.rollingmusic.cn/uploads/images/1230_7%20(1).jpg" data-cke-saved-src="/uploads/images/1230_7%20(1).jpg"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 500px" alt="" src="http://www.rollingmusic.cn/uploads/images/1230_5.jpg" data-cke-saved-src="/uploads/images/1230_5.jpg"></p>', 5, '', '2017-02-18 23:12:15'),
(16, 1, '2015-8-14 Camp C 结束汇报演出', 'Camp C Rock Band Performance', 0, '<p><span style="FONT-SIZE: 16px">&nbsp; 2015年8月14日，</span><span style="FONT-SIZE: 16px">Camp C的毕业演出有2支乐队进行，第一支乐队名叫“五”，是由5个初级小学员组成的乐队，第二个是由5个中级学员组成的乐队，名叫“尖峰”，</span><span style="FONT-SIZE: 16px">他们都同时学习了2周的时间，给家长和他们的朋友带来了非常经常的演出，所有人都为他们感到骄傲。</span></p><p data-cke-expando="undefined"><span style="FONT-SIZE: 16px" data-cke-expando="undefined"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 305px" alt="" src="/uploads/images/%E5%B0%81%E9%9D%A2logo.jpg" data-cke-saved-src="/uploads/images/%E5%B0%81%E9%9D%A2logo.jpg"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 282px" alt="" src="/uploads/images/%E5%B1%8F%E5%B9%95%E5%BF%AB%E7%85%A7%202015-09-17%20%E4%B8%8B%E5%8D%8803_41_10%201.jpg" data-cke-saved-src="/uploads/images/%E5%B1%8F%E5%B9%95%E5%BF%AB%E7%85%A7%202015-09-17%20%E4%B8%8B%E5%8D%8803_41_10%201.jpg"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 313px" alt="" src="/uploads/images/%E5%B1%8F%E5%B9%95%E5%BF%AB%E7%85%A7%202015-09-17%20%E4%B8%8B%E5%8D%8803_41_16.jpg" data-cke-saved-src="/uploads/images/%E5%B1%8F%E5%B9%95%E5%BF%AB%E7%85%A7%202015-09-17%20%E4%B8%8B%E5%8D%8803_41_16.jpg"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 281px" alt="" src="/uploads/images/%E5%B1%8F%E5%B9%95%E5%BF%AB%E7%85%A7%202015-09-17%20%E4%B8%8B%E5%8D%8803_41_35.jpg" data-cke-saved-src="/uploads/images/%E5%B1%8F%E5%B9%95%E5%BF%AB%E7%85%A7%202015-09-17%20%E4%B8%8B%E5%8D%8803_41_35.jpg"></span></p><p><br></p>', '<p><span style="FONT-SIZE: 16px">14<sup>th</sup>&nbsp;Aug 2015 Camp C Rock Band performance</span></p><p><span style="FONT-SIZE: 16px">&nbsp; &nbsp; Camp C final performance, this &nbsp;time we got 2 different level bands, one starter, named “the five”, another midi-level, named “the summit”. Both of them studied for 2 weeks . &nbsp;They gave such a fantastic show, made their parents and friends so proud of them.</span></p><p data-cke-expando="undefined"><span style="FONT-SIZE: 16px" data-cke-expando="undefined"><span style="FONT-SIZE: 16px" data-cke-expando="undefined"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 305px" alt="" src="http://www.rollingmusic.cn/uploads/images/%E5%B0%81%E9%9D%A2logo.jpg" data-cke-saved-src="/uploads/images/%E5%B0%81%E9%9D%A2logo.jpg"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 282px" alt="" src="http://www.rollingmusic.cn/uploads/images/%E5%B1%8F%E5%B9%95%E5%BF%AB%E7%85%A7%202015-09-17%20%E4%B8%8B%E5%8D%8803_41_10%201.jpg" data-cke-saved-src="/uploads/images/%E5%B1%8F%E5%B9%95%E5%BF%AB%E7%85%A7%202015-09-17%20%E4%B8%8B%E5%8D%8803_41_10%201.jpg"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 313px" alt="" src="http://www.rollingmusic.cn/uploads/images/%E5%B1%8F%E5%B9%95%E5%BF%AB%E7%85%A7%202015-09-17%20%E4%B8%8B%E5%8D%8803_41_16.jpg" data-cke-saved-src="/uploads/images/%E5%B1%8F%E5%B9%95%E5%BF%AB%E7%85%A7%202015-09-17%20%E4%B8%8B%E5%8D%8803_41_16.jpg"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 281px" alt="" src="http://www.rollingmusic.cn/uploads/images/%E5%B1%8F%E5%B9%95%E5%BF%AB%E7%85%A7%202015-09-17%20%E4%B8%8B%E5%8D%8803_41_35.jpg" data-cke-saved-src="/uploads/images/%E5%B1%8F%E5%B9%95%E5%BF%AB%E7%85%A7%202015-09-17%20%E4%B8%8B%E5%8D%8803_41_35.jpg"></span></span></p><p><br></p>', 6, '', '2017-02-18 23:18:32'),
(17, 1, '2015-8-15 学员乐队参加迷笛音乐节孩子们的迷笛表演', '2015-8-15 Students'' band perform on MIDI Kids Stage', 0, '<p><span style="FONT-SIZE: 16px">&nbsp; 2015年8月15日，学员乐队第一次参加2015迷笛音乐节“孩子们的迷笛”舞台表演。</span></p><p data-cke-expando="undefined"><span style="FONT-SIZE: 16px" data-cke-expando="undefined"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 334px" alt="" src="/uploads/images/395A8963logo.jpg" data-cke-saved-src="/uploads/images/395A8963logo.jpg"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 334px" alt="" src="/uploads/images/%E5%B0%81%E9%9D%A2%E5%9B%BE(2).jpg" data-cke-saved-src="/uploads/images/%E5%B0%81%E9%9D%A2%E5%9B%BE(2).jpg"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 334px" alt="" src="/uploads/images/395A9011.jpg" data-cke-saved-src="/uploads/images/395A9011.jpg"></span></p>', '<p data-cke-expando="undefined"><br></p><p data-cke-expando="undefined"><span data-cke-expando="undefined" style="font-size: 16px;"><span style="font-size:14px;"><span style="font-family: ''Trebuchet MS''; text-align: justify; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;">&nbsp; &nbsp; On August 8th, 2015, the student band gave their first show in MIDI Festival 2015 on MIDI Kids’ Stage.</span></span><img alt="" src="/uploads/images/395A8963logo.jpg" data-cke-saved-src="/uploads/images/395A8963logo.jpg" style="margin: 10px 320px; width: 500px; height: 334px;"><img alt="" src="/uploads/images/%E5%B0%81%E9%9D%A2%E5%9B%BE(2).jpg" data-cke-saved-src="/uploads/images/%E5%B0%81%E9%9D%A2%E5%9B%BE(2).jpg" style="margin: 10px 320px; width: 500px; height: 334px;"><img alt="" src="/uploads/images/395A9011.jpg" data-cke-saved-src="/uploads/images/395A9011.jpg" style="margin: 10px 320px; width: 500px; height: 334px;"></span></p>', 7, '', '2017-02-18 23:23:55'),
(18, 1, '2015-10-24 参与碧云社区国际家庭日活动', '2015-10-24  Green city Annual Family Day', 0, '<P><SPAN style="FONT-SIZE: 16px">&nbsp; 2015年10月24日，</SPAN><SPAN style="FONT-SIZE: 16px">Rolling music Band 参加上海碧云社区国际家庭日的演出 。我们邀请了来自北京中国资深的音乐节刘青和他的家庭乐队：条形码来到上海进行演出，同时为孩子们带来了大师班的讲座。<IMG style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 359px" alt="" src="/uploads/images/%E5%B0%81%E9%9D%A2%E5%9B%BE_1.jpg" data-cke-saved-src="/uploads/images/%E5%B0%81%E9%9D%A2%E5%9B%BE_1.jpg"><IMG style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="/uploads/images/1211_12_1.jpg" data-cke-saved-src="/uploads/images/1211_12_1.jpg"><IMG style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="/uploads/images/1211_4_1.jpg" data-cke-saved-src="/uploads/images/1211_4_1.jpg"></SPAN></P>\r\n<P></P>', '<P><SPAN style="FONT-SIZE: 16px">&nbsp;&nbsp;&nbsp; 24<SUP>th</SUP>&nbsp;Oct. Rolling music Band was invited to Green City 12<SUP>th</SUP>&nbsp;Annual Family Day. &nbsp;Rolling music &nbsp;kids band joined the performance. And we invited &nbsp;musician Liu Qin’s family band Bar Code to perform on the stage as well.&nbsp;&nbsp;<IMG style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 359px" alt="" src="http://www.rollingmusic.cn/uploads/images/%E5%B0%81%E9%9D%A2%E5%9B%BE_1.jpg" data-cke-saved-src="/uploads/images/%E5%B0%81%E9%9D%A2%E5%9B%BE_1.jpg"><IMG style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="http://www.rollingmusic.cn/uploads/images/1211_12_1.jpg" data-cke-saved-src="/uploads/images/1211_12_1.jpg"><IMG style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 375px" alt="" src="http://www.rollingmusic.cn/uploads/images/1211_4_1.jpg" data-cke-saved-src="/uploads/images/1211_4_1.jpg"></SPAN></P>', 8, '', '2017-02-18 23:29:05'),
(19, 1, '2015-10-25 参与“上海妈妈”国际家庭日活动', '2015-10-25  Shanghai Mama’s International Family Day', 0, '<p><span style="FONT-SIZE: 16px">&nbsp; 2015年10月25日</span><span style="FONT-SIZE: 16px">，万圣节当天，Rolling music带着铃铛、纸盘、各种豆子、彩色的颜料来到上海妈妈国际家庭日，带着孩子们一起制作他们的手工乐器。今天，每个孩子都是音乐家。</span></p><p data-cke-expando="undefined"><span style="FONT-SIZE: 16px" data-cke-expando="undefined"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 379px" alt="" src="/uploads/images/%E5%B0%81%E9%9D%A2%E5%9B%BE(3).jpg" data-cke-saved-src="/uploads/images/%E5%B0%81%E9%9D%A2%E5%9B%BE(3).jpg"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 667px" alt="" src="/uploads/images/1211_8.jpg" data-cke-saved-src="/uploads/images/1211_8.jpg"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 667px" alt="" src="/uploads/images/1211_6.jpg" data-cke-saved-src="/uploads/images/1211_6.jpg"></span></p><p><br></p>', '<p>&nbsp; &nbsp;&nbsp;<span style="font-size: 16px;">25</span><sup>th</sup><span style="font-size: 16px;">&nbsp;Oct on Halloween，Rolling music brought mini ring bells, color pens, balloons, beans and paper plates, to take part in Shanghai Mama’s International Family. &nbsp;The children make their own instruments. Fun of music, fun of rolling music !</span></p><p data-cke-expando="undefined"><span style="FONT-SIZE: 16px" data-cke-expando="undefined"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 379px" alt="" src="http://www.rollingmusic.cn/uploads/images/%E5%B0%81%E9%9D%A2%E5%9B%BE(3).jpg" data-cke-saved-src="/uploads/images/%E5%B0%81%E9%9D%A2%E5%9B%BE(3).jpg"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 667px" alt="" src="http://www.rollingmusic.cn/uploads/images/1211_8.jpg" data-cke-saved-src="/uploads/images/1211_8.jpg"><img style="MARGIN: 10px 320px; WIDTH: 500px; HEIGHT: 667px" alt="" src="http://www.rollingmusic.cn/uploads/images/1211_6.jpg" data-cke-saved-src="/uploads/images/1211_6.jpg"></span></p>', 9, '', '2017-02-18 23:34:56');

