--
-- 表的结构 `teacher`
--

CREATE TABLE IF NOT EXISTS `teacher` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `username` varchar(128) NOT NULL,
  `name` varchar(64) NOT NULL COMMENT '姓名',
  `en_name` varchar(128) NOT NULL COMMENT '英文名',
  `password` varchar(64) NOT NULL,
  `numbe` varchar(64) NOT NULL COMMENT '教师编号',
  `mobile` varchar(64) NOT NULL COMMENT '电话',
  `email` varchar(128) NOT NULL,
  `gender` char(16) NOT NULL COMMENT '性别',
  `instrument` varchar(64) NOT NULL COMMENT '乐器',
  `lang` varchar(64) NOT NULL COMMENT '上课语言',
  `thumb` varchar(128) NOT NULL COMMENT '头像',
  `desc` text NOT NULL COMMENT '简介',
  `en_desc` text NOT NULL,
  `en_country` varchar(32) NOT NULL,
  `country` varchar(32) NOT NULL,
  `created` varchar(128) NOT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`),
  KEY `name` (`name`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=16 ;

--
-- 转存表中的数据 `teacher`
--

INSERT INTO `teacher` (`id`, `username`, `name`, `en_name`, `password`, `numbe`, `mobile`, `email`, `gender`, `instrument`, `lang`, `thumb`, `desc`, `en_desc`, `en_country`, `country`, `created`) VALUES
(1, 'sam', 'Sam', 'Sam', '5fd1e22501d81b8bbd7c2f86f00e95da', 'T000001', '15021871481', 'sam@rollingmusic.cn', '0', '1,7', '英文&中文', '/uploads/201612032507e69XJhuwKI.jpg', '<p><span style="font-size: 20px;"><em><strong><span style="font-family: arial,helvetica,sans-serif;"><span style="font-size: 26px;">沙迷机</span></span></strong></em></span></p><p><span style="font-size: 20px;"><span style="font-family: arial,helvetica,sans-serif;"><em><strong>英国</strong></em></span></span></p><p><span style="font-size: 16px;"><span style="font-family: arial,helvetica,sans-serif;">&nbsp; &nbsp; Sam Gregory 从15岁年开始学鼓，自此，鼓就是他的声音，乐队就是他的生命。他热爱金属，爵士、Hip Hop,&nbsp;雷鬼，古典乐等不同类型的音乐文化。在英国创办知名乐队Downslide。现任5支乐队鼓手，已发行多张EP和原创单曲。</span></span></p><p><span style="font-size: 16px;"><span style="font-family: arial,helvetica,sans-serif;">&nbsp; &nbsp; 他在The University of Huddersfield完成流行音乐学位后，又在谢菲尔德大学研读，并在摇滚音乐学院任教，为不同年龄的学生开办乐队工作坊及传递乐队文化与乐趣。</span></span></p><p><br></p><div><br></div>', '<section style="margin: 0px; padding: 0px; color: rgb(62, 62, 62); font-family: 微软雅黑; font-size: 16px; -ms-word-wrap: break-word !important; max-width: 100%; box-sizing: border-box;"><p style="margin: 0px 0px 5px; padding: 0px; text-align: justify; line-height: 25.6px; clear: both; font-size: 14px; -ms-word-wrap: break-word !important; min-height: 1em; max-width: 100%; box-sizing: border-box !important;"><em><strong><span style="font-size: 20px;"><span style="font-size: 26px;">S</span>am </span></strong></em><br type="_moz"></p><p style="margin: 0px 0px 5px; padding: 0px; text-align: justify; line-height: 25.6px; clear: both; font-size: 14px; -ms-word-wrap: break-word !important; min-height: 1em; max-width: 100%; box-sizing: border-box !important;"><em><strong><span style="font-size: 20px;">England</span></strong></em></p><p style="margin: 0px 0px 5px; padding: 0px; text-align: justify; line-height: 25.6px; clear: both; font-size: 14px; -ms-word-wrap: break-word !important; min-height: 1em; max-width: 100%; box-sizing: border-box !important;"><span style="font-size: 16px;">&nbsp; &nbsp; Sam Gregory started playing the drums at the age of 15. He found his voice in hitting the drums, and that started his life playing in bands. He particularly loves Metal, Drum n Bass, Jazz, Hip Hop, Reggae, and Classical music. He studied music at the Sheffield College and gained a BA hons Popular Music at The University of Huddersfield. He also formed the UK band, Downslide.</span></p></section><p style="margin: 0px 0px 5px; padding: 0px; text-align: justify; color: rgb(62, 62, 62); line-height: 25.6px; clear: both; font-family: 微软雅黑; font-size: 14px; -ms-word-wrap: break-word !important; min-height: 1em; max-width: 100%; box-sizing: border-box !important;"><span style="font-size: 16px;">&nbsp; &nbsp; He taught drums to students of different ages at The Wreck Room and School of Rock Music School, using the Trinity Rock School and London College of Music curricula. He ran band workshops, which were designed to promote interest with the younger ages, and develop skills and band communication with the older ages. He has&nbsp; appeared on recordings released worldwide on Rising Records, Sony Italy and Zhuluhefeng. Sam has gained a wealth of performance experience having toured Europe and China with the bands, Downslide, Cause of Denial, Blue Magpie and TMOOS.</span></p>', 'England', '英国', '2016-07-21 13:30:10'),
(6, 'caizhizheng', '蔡志铮', 'Zhizheng Cai', '02ba61064fbf3368567bb27eb4193e42', 'T000007', '18930720773', 'czz@rollingmusic.cn', '0', '2,3,7,24', '中文', '/uploads/201702153322WLEOYPQDna.png', '<p><span style="font-family: arial,helvetica,sans-serif;"><span style="font-size: 20px;"><em><strong>蔡老师</strong></em></span></span><br type="_moz"></p><p><span style="font-family: arial,helvetica,sans-serif;"><span style="font-size: 20px;"><em><strong>中国</strong></em></span></span></p><p style="padding: 0px; color: rgb(0, 0, 0); clear: both; font-family: 微软雅黑; margin-top: 0px; margin-bottom: 0px;"><span style="font-size: 16px;"><span style="margin: 0px; padding: 0px;">&nbsp; &nbsp; 撰写《吉他演奏就是这么简单》教材；主要参演、创作、录制的作品有：《妈妈咪呀》、《杜拉拉》、《番茄不简单》、《撒娇女王》、《资本•论》、《你是我的孤独》、《仲夏》、《spin》、《我和春天有个约会》、《寻找初恋》、《苏州河》、《香水》、《南京攻略》、《www.com》、《红绿变换曲》等三十几部作品。</span></span></p>', '<p><br></p><p><span style="font-size: 20px;"><span style="font-family: arial,helvetica,sans-serif;"><em><strong><span style="color: rgb(62, 62, 62);">Mr&nbsp;</span>​<span style="color: rgb(62, 62, 62);">Cai&nbsp;</span></strong></em></span></span><br type="_moz"></p><p><span style="font-size: 20px;"><span style="font-family: arial,helvetica,sans-serif;"><em><strong><span style="color: rgb(62, 62, 62);">China</span></strong></em></span></span></p><p><span style="font-size: 16px;"><span style="font-family: arial,helvetica,sans-serif;"><span style="color: rgb(62, 62, 62);">&nbsp; &nbsp; Zhizheng&nbsp;</span>​<span style="color: rgb(62, 62, 62);">Cai&nbsp;</span><span style="color: rgb(62, 62, 62);">graduated from the Beijing Midi School of Music and the Central Conservatory of Music. He is mainly engaged in live music accompaniment, recording, and writing. He has over 10 years of teaching experience. Zhizheng Cai has written “Guitar Playing Is As Simple As” teaching material. He has composed pieces for about 30 films and musicals. His recorded works include <em>Mamma Mia, Story of Du LaLa, Simple Tomato, Spoiled Queen, Capital On, You Are My Loneliness, Midsummer, Spin, I Have a Date With Spring, Looking for Love, and Suzhou River.&nbsp;</em></span></span></span></p>', 'China', '中国', '2017-02-06 11:40:43'),
(8, 'mark', 'Mark Hodnett', 'Mark Hodnett', '3e86e32e57e99f3cfeee9dcedaa7c255', 'T000009', '13262535317', 'mus4mus@yahoo.co.uk', '0', '2', '英语&中文', '/uploads/201702152970WvgkKbjxmT.jpg', '<p><span style="font-size: 20px;"><span style="font-family: arial,helvetica,sans-serif;"><em><strong>Mark Hodnett</strong></em></span></span></p><p><span style="font-size: 20px;"><span style="font-family: arial,helvetica,sans-serif;"><em><strong>英国</strong></em></span></span></p><p><span style="font-family: arial,helvetica,sans-serif;"><span style="font-size: 16px;">&nbsp; &nbsp; Mark Hodnett来自英国，利兹大学（U.K）音乐作曲硕士学位及世界音乐学本科学位。曾任中国好声音吉他手和上海音乐学院的吉他教师。&nbsp;除教学外，还活跃于北京、戛纳、重庆等地的音乐节演出中。</span></span></p>', '<p><span style="font-size: 20px;"><span style="font-family: arial,helvetica,sans-serif;"><em><strong><span style="text-align: justify; -webkit-text-stroke-color: rgb(0, 0, 0);">Mark Hodnett</span></strong></em></span></span></p><p><span style="font-size: 20px;"><span style="font-family: arial,helvetica,sans-serif;"><em><strong><span style="text-align: justify; -webkit-text-stroke-width: initial; -webkit-text-stroke-color: rgb(0, 0, 0);">England</span></strong></em></span></span></p><p><span style="font-size: 16px;"><span style=''text-align: justify; font-family: "Trebuchet MS"; -webkit-text-stroke-width: initial; -webkit-text-stroke-color: rgb(0, 0, 0);''>Mark Hodnett is from Staffordshire, United Kingdom. In Shanghai he has been a guitarist working for the Voice of China/Sing my Song performing many live shows in Shanghai and elsewhere. He has also performed at Music festivals in Mongolia, ChongChing and Music Expos in Beijing, Cannes (France) and Shanghai.</span></span></p><p style=''margin: 0px 0px 10px; text-align: justify; line-height: normal; font-family: "Trebuchet MS"; font-size: 11px; -webkit-text-stroke-width: initial; -webkit-text-stroke-color: rgb(0, 0, 0);''><span style="font-size: 16px;"><span style="font-kerning: none;">In addition to his live work he has recorded music for media companies here in Shanghai. He also has performed as a Dj at Mao Livehouse, DADA nightclub and several times as a guest DJ for the radio station UDANCE.net.</span></span></p><p style=''margin: 0px 0px 10px; text-align: justify; line-height: normal; font-family: "Trebuchet MS"; font-size: 11px; -webkit-text-stroke-width: initial; -webkit-text-stroke-color: rgb(0, 0, 0);''><span style="font-size: 16px;"><span style="font-kerning: none;">&nbsp;He holds a Masters degree in Music composition from Leeds University (U.K) along with an undergraduate degree in World Musics. He has worked as a guitar teacher for the Shanghai School of Music.</span></span></p>', 'England', '英国', '2017-02-06 12:23:04'),
(11, '欧晨宇', '欧晨宇', 'Mr Ou', '040ccd61ace23136916c87bccb8ccb05', 'T0000010', '18501680281', 'xiaoou@rollingmusic.cn', '0', '1,3,4,7', '中文', '/uploads/201707034999ix6pbYskLA.jpg', '<p><em><strong><span style="font-size: 26px;">欧老师</span></strong></em><br type="_moz"></p><p><em><strong><span style="font-size: 20px;">中国</span></strong></em><br type="_moz"></p><p><span style="font-size: 16px;">&nbsp; 于北京现代音乐学院精研打击乐，得到世界级架子鼓大师Steve Smith，Dave Weckl，Lzumi 等人指点。担任《你是我的孤独》《谋杀歌谣》等多部音乐剧鼓手。与曹格、凤凰传奇、张玮、阿牛等众多一线艺人合作。教学经验丰富，亲和幽默，善于结合数理化等交叉学科阐释音乐艺术，深受学生喜爱。</span></p><p><br></p>', '<p><span style="font-size: 26px;"><strong><em><span style="font-size: 24px;"></span>Mr ou<br type="_moz"></em></strong></span></p><div style=''color: rgb(85, 85, 85); font-family: "Segoe UI", Tahoma, Helvetica, sans-serif; font-size: 13px;'' paragraphname="paragraph2"><span id="noHighlight_0.7083959239535034">China</span><br type="_moz"></div><div style=''color: rgb(85, 85, 85); font-family: "Segoe UI", Tahoma, Helvetica, sans-serif; font-size: 13px;'' paragraphname="paragraph4"><br></div><div style=''color: rgb(85, 85, 85); font-family: "Segoe UI", Tahoma, Helvetica, sans-serif; font-size: 13px;'' paragraphname="paragraph4"><span id="noHighlight_0.6068604635074735">Lil O studied at the Beijing Modern Conservatory of percussion, and he has attended various master classes taught by world-class drummers such as Steve Smith, Dave Weckl, Lzumi and others. Lil O has also served as the drummer of musicals such as "You Are My Solitude" "Murder ballad" and many more. Lil O has also been the drummer in bands featuring arists like Cao, Phoenix Legends, Wei, Aniu and more. Lil O has a wealth experience in music studies, a good sense of humor, and he can easily connect with students to improve their musical undersanding while still having clear control over their discipline.</span><br type="_moz"></div>', 'China', '中国', '2017-07-03 19:19:51'),
(12, '项国君', '项国君', 'Mr Xiang', '61895e5d27bd19a653136964c5d74618', 'T000011', '18616811850', 'xiang@rollingmusic.cn', '0', '1,2,3,4,7,24', '中国', '/uploads/201707032503VrQzC6Gisu.jpg', '<p><em><strong><span style="font-size: 26px;">项老师</span></strong></em><br type="_moz"></p><p><em><strong><span style="font-size: 20px;">中国</span></strong></em></p><p><span style="font-size: 16px;">&nbsp; 擅长吉他、钢琴、尤克里里等乐器，17年演奏经验和各乐器考级教学经验丰富，并均获得最高级别认证。擅长将乐器教学与生活趣事和时尚潮流相连接，实用的演奏技巧和练习模式能帮助学生高效完成学习目标，提高演奏能力，深受学生喜爱和好评。同时，他幽默风趣，富有亲和力和感召力，备受小朋友喜爱。</span></p>', '<div style=''color: rgb(85, 85, 85); font-family: "Segoe UI", Tahoma, Helvetica, sans-serif; font-size: 13px;'' paragraphname="paragraph1">Mr Xiang<br type="_moz"></div><p><br></p><div style=''color: rgb(85, 85, 85); font-family: "Segoe UI", Tahoma, Helvetica, sans-serif; font-size: 13px;'' paragraphname="paragraph3"><span id="noHighlight_0.6824865231756121">China</span></div><p><br></p><div style=''color: rgb(85, 85, 85); font-family: "Segoe UI", Tahoma, Helvetica, sans-serif; font-size: 13px;'' paragraphname="paragraph5"><span id="noHighlight_0.1091876458376646">Lil Xiang is a very versatile musician, he can play the guitar, piano, Ukulele and many other instruments. Lil Xiang has a thorough understanding of various musical examination programs, of which he personally has acheived many high level certificates for, in addition to his&nbsp;</span>17 years of experience in the music industr. Lil Xiang is able to connect music as a study to modern life and trends, his methods of teaching practical playing skills &nbsp;help students achieve their musical goals efficiently. At the same time, he is humorous, charismatic, and loved by his students.</div><p><br></p>', 'China', '中国', '2017-07-03 20:03:53'),
(14, '胡馨月', '胡馨月', 'Hannah', 'a545f9ab50b6d9fe66b9f65664de0ff7', 'T000013', '15021628950', 'hannah@rollingmusic.cn', '1', '1', '中文', '/uploads/201707037041NuLHSTGXI9.jpg', '<p><em><strong><span style="font-size: 26px;">胡老师</span></strong></em></p><p><em><strong><span style="font-size: 20px;">中国</span></strong></em></p><p><span style="font-size: 16px;">&nbsp; 儿童趣味电鼓教学华东地区培训师，擅长架子鼓、吉他，钢琴等乐器。胡老师耐心细致，对儿童音乐教育具有丰富的经验。同时，作为青年女鼓手，她携乐队参加草莓音乐节，星外滩音乐节、泛声音乐节等大小舞台，演出经验丰富。</span></p>', '<p>Mriss Hu<br type="_moz"></p><div style=''color: rgb(85, 85, 85); font-family: "Segoe UI", Tahoma, Helvetica, sans-serif; font-size: 13px;'' paragraphname="paragraph2"><span id="noHighlight_0.7566638011485338">China</span></div><br style=''color: rgb(85, 85, 85); font-family: "Segoe UI", Tahoma, Helvetica, sans-serif; font-size: 13px;''><div style=''color: rgb(85, 85, 85); font-family: "Segoe UI", Tahoma, Helvetica, sans-serif; font-size: 13px;'' paragraphname="paragraph4"><span id="noHighlight_0.1648560781031847">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Miss Hu&nbsp;is the official trainer of Juior electric drum studies in the East China region, she is also a very versitile musician having thoroughly studied drums, guitar, piano and other instruments. Lil hana is a patient teacher, and has rich experience in teaching music to children. At the same time, as a drummer, she has participated in the Strawberry Music festival, the Shanghai Bund Music festival, Pan Sound Music festival and many other music fesivals.</span><br type="_moz"></div>', 'China', '中国', '2017-07-03 20:34:05'),
(15, '邱实', '邱实', 'Miss Qiu', '5c6e6c6a61e19a31f6c17d30c9e3ab0f', 'T000015', '13585963445', 'qs@rollingmusic.cn', '1', '4', '中文', '/uploads/201707036719D0Zo61RrAj.jpg', '<p><em><strong><span style="font-size: 26px;">邱老师</span></strong></em></p><p><em><strong><span style="font-size: 20px;">中国</span></strong></em></p><p><span style="font-size: 16px;">&nbsp; 毕业于沈阳音乐学院，主修钢琴专业。学士学位，自幼学习钢琴，从小系统学习专业音乐知识。先后师从于中国音乐学院钢琴系主任张柯瑶教授，德国钢琴教育家沃尔夫岗教授。在教学中，能够注重孩子结合音乐与听、唱、理论多管齐下的培养，在多年的教学经验中总结出自己的一套方法。能够调动学生的学习兴趣，教学风格严谨，同时注重趣味性，深受学生和家长的喜爱。</span></p>', '<p>Miss Qu</p><br style=''color: rgb(85, 85, 85); font-family: "Segoe UI", Tahoma, Helvetica, sans-serif; font-size: 13px;''><div style=''color: rgb(85, 85, 85); font-family: "Segoe UI", Tahoma, Helvetica, sans-serif; font-size: 13px;'' paragraphname="paragraph2"><span id="noHighlight_0.013246491784229875">China</span></div><br style=''color: rgb(85, 85, 85); font-family: "Segoe UI", Tahoma, Helvetica, sans-serif; font-size: 13px;''><div style=''color: rgb(85, 85, 85); font-family: "Segoe UI", Tahoma, Helvetica, sans-serif; font-size: 13px;'' paragraphname="paragraph4"><span id="noHighlight_0.2630148429889232">Lil Chiu graduated from the Shenyang Conservatory of Music, majoring in piano. She studied piano from from a young age, in addition to also studying professional music information. Lil Chiu has studied with Proffessor Zhang, director of the piano Department of China Conservatory of Music, and also Professor Wolff, piano educator from Germany. In teaching, she helps her students understand music through listening, sings, and looking deeper into music theory, after her many years of experience as a music educator she has developed her own unique teaching style. She is able to motivate interest from her students to learn music and at the same time she pay attention to the the student''s interest and molds her teaching based on this. She is deeply loved by her students and their parents.</span></div>', 'China', '中国', '2017-07-03 20:51:01');

